    // Cleanup temporary directory
    try {
      fs.rmSync(tmpBase, { recursive: true, force: true });
      logger.info('Agent run finished. Temporary directory cleaned up.');
    } catch (e) {
      logger.warn(`Failed to cleanup temporary directory '${tmpBase}': ${e}`);
    }
  }
}
