        "content": "import { ipcMain, webContents } from 'electron';
import IPC_HANDLER_KEYS from '../../ipcHandlersKeys.js';
import path from 'node:path';
import fs from 'node:fs';

// Load external package factory-ts from sibling repo ../thefactory-tools
// Use dynamic import to avoid issues with CJS/ESM resolution in Electron
let factoryTs;
async function loadFactory()
{
  if (factoryTs) return factoryTs;
  const cwd = process.cwd();
  const distEsm = path.resolve(cwd, '../thefactory-tools/dist/index.js');
  const distCjs = path.resolve(cwd, '../thefactory-tools/dist/index.cjs');
