{"thoughts":"I will inspect the repository structure, then add a new SQLite database module at src/db/database.ts. It will provide initialization, table creation with standard columns (id, created_at, updated_at, keywords TEXT, embedding BLOB), and generic query helpers. I will also ensure to update docs/FILE_ORGANISATION.md if adding a new major directory (src/db).","tool_calls":[{"tool_name":"list_files","arguments":{"path":"."}},{"tool_name":"list_files","arguments":{"path":"src"}},{"tool_name":"list_files","arguments":{"path":"src/tools"}},{"tool_name":"write_file","arguments":{"filename":"src/db/database.ts","content":"/*
  Generic SQLite Database Module
  - Manages a singleton connection to a SQLite database file within the repo
  - Provides initialization, standardized table creation helpers, and query utilities
  - Standardized tables include columns for keyword search and embeddings

  Dependencies: Uses better-sqlite3 for sync, fast, and robust SQLite operations.
*/

import Database from 'better-sqlite3'
import fs from 'fs'
import path from 'path'

export type SqlParams = Record<string, any> | any[]

export type ColumnSpec = {
  name: string
  type: string
  constraints?: string // e.g., 'NOT NULL', 'UNIQUE', 'DEFAULT 0'
}

export type TableSpec = {
  name: string
  // Additional columns to include beyond the standardized columns
  columns?: ColumnSpec[]
  // Unique constraints or other table-level constraints
  tableConstraints?: string[]
  // Create an index on keywords for LIKE search
  indexKeywords?: boolean
  // Create vss0 virtual table for embeddings if available in environment (optional)
  // Note: by default we store embeddings as BLOB on the main table; for semantic search,
  // you can enable a companion vss/FTS module later if needed.
}

export type DBConfig = {
  // path to directory for db files (default: .data)
  dataDir?: string
  // main db file name (default: project.db)
  filename?: string
  // SQLite PRAGMA settings
  pragmas?: { [key: string]: string | number }
}

let dbInstance: Database.Database | null = null
let dbPath: string | null = null

function ensureDir(dir: string) {
  if (!fs.existsSync(dir)) {
    fs.mkdirSync(dir, { recursive: true })
  }
}

export function getDbPath(projectRoot: string, config?: DBConfig) {
  const dataDir = config?.dataDir ?? '.data'
  const filename = config?.filename ?? 'project.db'
  return path.resolve(projectRoot, dataDir, filename)
}

export function initDatabase(projectRoot: string, config?: DBConfig) {
  if (dbInstance) return dbInstance

  const targetPath = getDbPath(projectRoot, config)
  const dir = path.dirname(targetPath)
  ensureDir(dir)

  const db = new Database(targetPath)
  db.pragma('journal_mode = WAL')
  db.pragma('synchronous = NORMAL')
  db.pragma('foreign_keys = ON')

  if (config?.pragmas) {
    for (const [k, v] of Object.entries(config.pragmas)) {
      db.pragma(`${k} = ${v}`)
    }
  }

  // Create a meta table to store simple key/value settings, including migrations
  db.exec(`CREATE TABLE IF NOT EXISTS __meta (
    key TEXT PRIMARY KEY,
    value TEXT
  )`)

  dbInstance = db
  dbPath = targetPath
  return db
}

export function getDb(): Database.Database {
  if (!dbInstance) {
    throw new Error('Database not initialized. Call initDatabase(projectRoot, config) first.')
  }
  return dbInstance
}

export function closeDatabase() {
  if (dbInstance) {
    dbInstance.close()
    dbInstance = null
    dbPath = null
  }
}

// Standardized base columns for entity tables.
// - id: primary key
// - created_at, updated_at: timestamps
// - keywords: TEXT field suitable for keyword search
// - embedding: BLOB field to store vector embeddings (e.g., Float32Array bytes)
export function getStandardColumns(): ColumnSpec[] {
  return [
    { name: 'id', type: 'TEXT', constraints: 'PRIMARY KEY' },
    { name: 'created_at', type: 'TEXT', constraints: 'NOT NULL' },
    { name: 'updated_at', type: 'TEXT', constraints: 'NOT NULL' },
    { name: 'keywords', type: 'TEXT' },
    { name: 'embedding', type: 'BLOB' },
  ]
}

export function createTable(spec: TableSpec) {
  const db = getDb()

  const cols: ColumnSpec[] = [...getStandardColumns(), ...(spec.columns ?? [])]
  const colDefs = cols
    .map((c) => `${c.name} ${c.type}${c.constraints ? ' ' + c.constraints : ''}`)
    .join(', ')

  const tableConstraints = spec.tableConstraints?.length
    ? ', ' + spec.tableConstraints.join(', ')
    : ''

  const sql = `CREATE TABLE IF NOT EXISTS ${spec.name} (${colDefs}${tableConstraints})`
  db.exec(sql)

  // Index on keywords for faster LIKE queries
  if (spec.indexKeywords) {
    db.exec(`CREATE INDEX IF NOT EXISTS idx_${spec.name}_keywords ON ${spec.name}(keywords)`)
  }
}

// Helper to insert or upsert rows with defaults for timestamps
export function upsert(
  table: string,
  data: Record<string, any>,
  conflictTarget: string = 'id'
) {
  const db = getDb()
  const nowIso = new Date().toISOString()
  const payload = {
    created_at: nowIso,
    updated_at: nowIso,
    ...data,
  }

  const columns = Object.keys(payload)
  const placeholders = columns.map((c) => `@${c}`).join(', ')
  const updates = columns
    .filter((c) => c !== conflictTarget && c !== 'created_at')
    .map((c) => `${c}=excluded.${c}`)
    .concat(['updated_at=excluded.updated_at'])
    .join(', ')

  const sql = `INSERT INTO ${table} (${columns.join(', ')}) VALUES (${placeholders})
    ON CONFLICT(${conflictTarget}) DO UPDATE SET ${updates}`

  const stmt = db.prepare(sql)
  return stmt.run(payload)
}

export function insert(table: string, data: Record<string, any>) {
  const db = getDb()
  const nowIso = new Date().toISOString()
  const payload = {
    created_at: nowIso,
    updated_at: nowIso,
    ...data,
  }
  const columns = Object.keys(payload)
  const placeholders = columns.map((c) => `@${c}`).join(', ')
  const sql = `INSERT INTO ${table} (${columns.join(', ')}) VALUES (${placeholders})`
  const stmt = db.prepare(sql)
  return stmt.run(payload)
}

export function update(table: string, data: Record<string, any>, whereClause: string, params: SqlParams = {}) {
  const db = getDb()
  const nowIso = new Date().toISOString()
  const payload = { ...data, updated_at: nowIso }
  const set = Object.keys(payload).map((k) => `${k}=@${k}`).join(', ')
  const sql = `UPDATE ${table} SET ${set} WHERE ${whereClause}`
  const stmt = db.prepare(sql)
  return stmt.run({ ...payload, ...(Array.isArray(params) ? {} : params) })
}

export function remove(table: string, whereClause: string, params: SqlParams = {}) {
  const db = getDb()
  const sql = `DELETE FROM ${table} WHERE ${whereClause}`
  const stmt = db.prepare(sql)
  return stmt.run(params)
}

export function select<T = any>(sql: string, params: SqlParams = {}): T[] {
  const db = getDb()
  const stmt = db.prepare(sql)
  return stmt.all(params) as T[]
}

export function selectOne<T = any>(sql: string, params: SqlParams = {}): T | undefined {
  const db = getDb()
  const stmt = db.prepare(sql)
  return stmt.get(params) as T | undefined
}

export function run(sql: string, params: SqlParams = {}) {
  const db = getDb()
  const stmt = db.prepare(sql)
  return stmt.run(params)
}

export function transaction<T>(fn: () => T): T {
  const db = getDb()
  const trx = db.transaction(fn)
  return trx()
}

// Basic helpers for keyword and embedding storage
export function toKeywordString(input: string | string[]): string {
  return Array.isArray(input) ? input.join(' ') : input
}

export function toEmbeddingBlob(vec: number[] | Float32Array | Buffer | null | undefined): Buffer | null {
  if (!vec) return null
  if (Buffer.isBuffer(vec)) return vec
  if (vec instanceof Float32Array) {
    return Buffer.from(new Uint8Array(vec.buffer))
  }
  if (Array.isArray(vec)) {
    const f32 = new Float32Array(vec)
    return Buffer.from(new Uint8Array(f32.buffer))
  }
  throw new Error('Unsupported embedding format')
}

// Simple keyword search helper (case-insensitive LIKE); caller should escape wildcards as needed.
export function keywordSearch(table: string, keywords: string, limit = 50) {
  const db = getDb()
  const pattern = `%${keywords.replace(/%/g, '\\%').replace(/_/g, '\\_')}%`
  const sql = `SELECT * FROM ${table} WHERE LOWER(keywords) LIKE LOWER(@pattern) LIMIT ${limit}`
  const stmt = db.prepare(sql)
  return stmt.all({ pattern })
}

// Naive semantic search using dot product or cosine similarity is not possible without a vector index.
// This function demonstrates how to scan and compute cosine similarity in JS if necessary.
// For large datasets consider a proper vector index or SQLite extensions.
export function semanticSearch(
  table: string,
  queryEmbedding: Float32Array,
  {
    topK = 10,
    embeddingColumn = 'embedding',
    selectColumns = '*',
  }: { topK?: number; embeddingColumn?: string; selectColumns?: string } = {}
) {
  const rows = select<any>(`SELECT ${selectColumns} FROM ${table} WHERE ${embeddingColumn} IS NOT NULL`)

  function cosineSim(a: Float32Array, b: Float32Array) {
    let dot = 0
    let na = 0
    let nb = 0
    const len = Math.min(a.length, b.length)
    for (let i = 0; i < len; i++) {
      const va = a[i]
      const vb = b[i]
      dot += va * vb
      na += va * va
      nb += vb * vb
    }
    if (na === 0 || nb === 0) return 0
    return dot / (Math.sqrt(na) * Math.sqrt(nb))
  }

  function bufferToF32(buf: Buffer): Float32Array {
    // Buffer came from Uint8Array(Float32Array.buffer)
    // Reconstruct Float32Array view
    const arr = new Uint8Array(buf.buffer, buf.byteOffset, buf.byteLength)
    const f32 = new Float32Array(arr.buffer, arr.byteOffset, Math.floor(arr.byteLength / 4))
    return new Float32Array(f32) // copy to detach from underlying Buffer
  }

  const scored = rows
    .map((r) => {
      const buf: Buffer | null = r[embeddingColumn]
      if (!buf) return null
      const emb = bufferToF32(buf)
      const score = cosineSim(queryEmbedding, emb)
      return { row: r, score }
    })
    .filter(Boolean) as { row: any; score: number }[]

  scored.sort((a, b) => b.score - a.score)
  return scored.slice(0, topK)
}

export function getDatabaseFilePath(): string | null {
  return dbPath
}
