{"thoughts":"Updating AgentRun type to include optional rating field and running tests.","tool_calls":[{"tool_name":"write_file","arguments":{"filename":"packages/factory-ts/src/types.ts","content":"export type Status = '+' | '-' | '~' | '?' | '=';
export interface Feature {
  id: string;
  status: Status;
  title: string;
  description: string;
  context: string[];
  plan?: string;
  acceptance?: string[];
  blockers?: string[];
  rejection?: string;
}
export interface Task {
  id: string;
  title: string;
  description: string;
  status: Status;
  features: Feature[];
  featureIdToDisplayIndex: Record<string, number>;
  blockers?: string[];
  rejection?: string;
}
export interface ProjectRequirement {
  id: number;
  status: Status;
  description: string;
  tasks: string[];
}
export interface ProjectSpec {
  id: string;
  title: string;
  description: string;
  path: string;
  repo_url: string;
  requirements: ProjectRequirement[];
  taskIdToDisplayIndex: Record<string, number>;
  metadata?: Record<string, any>;
}
export type CompletionMessageRole = 'system' | 'user' | 'assistant';
export type CompletionMessage = { role: CompletionMessageRole; content: string };
export type CompletionUsage = {
  promptTokens?: number;
  completionTokens?: number;
  totalTokens?: number;
  costUSD?: number;
  provider?: string;
  model?: string;
};
export type CompletionResponse = { message: { role: 'assistant'; content: string }; usage?: CompletionUsage };
export type CompletionClient = (req: { model: string; messages: CompletionMessage[]; response_format?: any }) => Promise<CompletionResponse>;
export type ToolCall = { tool_name?: string; tool?: string; name?: string; arguments?: any; parameters?: any };
export type ToolResult = { name: string; result: string };
export type AgentResponse = { thoughts?: string; tool_calls?: ToolCall[] };

export type LLMConfig = {
  model: string;
  provider: string; // e.g., openai, azure, together, groq, openrouter, ollama, custom
  apiKey: string;
  apiBaseUrl: string;

  // extra provider fields (azure etc.) tolerated
  [key: string]: any;
};

export type AgentType = 'developer' | 'tester' | 'planner' | 'contexter' | 'speccer';
export type AgentRun = {
  agent: AgentType;
  projectId: string;
  taskId: string;
  featureId?: string;
  llmConfig: LLMConfig;
  rating?: 'up' | 'down' | null;
  options?: {
    budgetUSD?: number;
    metadata?: Record<string, any>;
  };
};
export type RunEvent = { type: string; payload?: any };

// Live Data Service Types
export type LiveDataServiceFreshnessPolicy = 'daily' | 'weekly' | 'monthly';
export type LiveDataServiceAutoUpdateTrigger = 'onAppLaunch' | 'scheduled';

export interface LiveDataServiceAutoUpdateSettings {
  enabled: boolean;
  trigger: LiveDataServiceAutoUpdateTrigger;
}

export interface LiveDataService<TConfig = unknown> {
  id: string;
  name: string;
  description: string;
  lastUpdated: number; // Unix timestamp
  freshnessPolicy: LiveDataServiceFreshnessPolicy;
  autoUpdate: LiveDataServiceAutoUpdateSettings;
  config: TConfig; // Service-specific configuration
}
