const LIVEDATA_API = {
  subscribe: (callback) => {
    const listener = (_event, services) => callback(services);
    ipcRenderer.on(IPC_HANDLER_KEYS.LIVE_DATA_SUBSCRIBE, listener);
    return () => ipcRenderer.removeListener(IPC_HANDLER_KEYS.LIVE_DATA_SUBSCRIBE, listener);
