function shouldIgnoreCopy(relPath: string): boolean {
  // Ignore common build, cache, and IDE artifacts for TypeScript/Node/Electron projects
  // Preserve .git (do NOT ignore)
  const parts = relPath.split(path.sep);
  const name = parts[parts.length - 1] || relPath;

  // If path is within .git, do not ignore (preserve repo history/branches)
  if (parts.includes('.git')) return false;

  const dirIgnore = new Set([
    // Package managers and caches
    'node_modules', '.pnpm', '.pnp', '.pnp.cjs', '.pnp.js', '.npm', '.yarn', '.yarn-cache', '.yarnrc',
    // Build/output directories
    'dist', 'build', 'out', 'release', 'storybook-static',
    // Framework-specific
    '.next', 'next', '.nuxt', '.svelte-kit',
    // Bundlers and dev servers caches
    '.vite', '.rollup.cache', '.parcel-cache', '.webpack',
    // Test/coverage
    'coverage', '.nyc_output',
    // Monorepo/turbo caches
    '.turbo',
    // Generic caches
    '.cache', '.eslintcache', '.rpt2_cache', '.tscache',
    // Editors/IDE
    '.idea', '.vscode',
    // Python (from original mirror)
    'venv', '__pycache__',
    // Logs and temp
    'logs', 'tmp', 'temp'
  ]);

  // If any path segment matches an ignored dir name
  if (parts.some(p => dirIgnore.has(p))) return true;

  // Ignore specific file patterns
  const lower = name.toLowerCase();
  if (lower === '.ds_Store'.toLowerCase() || lower === 'thumbs.db') return true;
  if (lower.endsWith('.log')) return true; // npm/yarn/pnpm debug logs
  if (lower.endsWith('.pyc')) return true; // Python bytecode files
  if (lower.endsWith('.tsbuildinfo')) return true; // TypeScript incremental build info

  // Ignore Electron asar content markers when encountered as path segments
  if (parts.some(p => p.endsWith('.asar') || p.endsWith('.asar.unpacked'))) return true;

  return false;
}

async function copyTree(src: string, dest: string) {
  // Recursively copy, respecting ignore patterns and skipping asar/virtual entries
  let stat: fs.Stats;
  try {
    stat = await fsp.stat(src);
  } catch (e: any) {
    throw new Error(`Source path does not exist or is not accessible: ${src} (${e?.code || e})`);
    }
  if (!stat.isDirectory()) throw new Error(`Source ${src} is not a directory`);
  await fsp.mkdir(dest, { recursive: true });

  const walk = async (from: string, to: string) => {
    let entries: fs.Dirent[] = [] as unknown as fs.Dirent[];
    try {
      entries = await fsp.readdir(from, { withFileTypes: true });
    } catch (e: any) {
      // Skip unreadable directories (e.g., asar virtuals)
      logger.warn(`copyTree: Skipping unreadable directory ${from}: ${e?.code || e}`);
      return;
    }
    for (const entry of entries) {
      const rel = path.relative(src, path.join(from, entry.name));
      if (shouldIgnoreCopy(rel)) continue;
      const srcPath = path.join(from, entry.name);
      const dstPath = path.join(to, entry.name);

      // Skip any path that traverses into an asar archive
      if (srcPath.includes('.asar')) continue;

      try {
        if (entry.isDirectory()) {
          await fsp.mkdir(dstPath, { recursive: true });
          await walk(srcPath, dstPath);
        } else if (entry.isSymbolicLink()) {
          // Resolve symlink target and copy contents (best-effort)
          try {
            const linkTarget = await fsp.readlink(srcPath);
            const resolved = path.resolve(path.dirname(srcPath), linkTarget);
            const st = await fsp.stat(resolved);
            if (st.isDirectory()) {
              await fsp.mkdir(dstPath, { recursive: true });
              await walk(resolved, dstPath);
            } else {
              await fsp.copyFile(resolved, dstPath);
            }
          } catch {
            // Fallback: copy file bytes of the link itself (as file)
            try { await fsp.copyFile(srcPath, dstPath); } catch {}
          }
        } else if (entry.isFile()) {
          await fsp.copyFile(srcPath, dstPath);
        }
      } catch (e: any) {
        // Skip problematic entries (e.g., ENOENT due to virtualized packaging paths)
        if (e && (e.code === 'ENOENT' || e.code === 'EBUSY' || e.code === 'EPERM')) {
          logger.warn(`copyTree: Skipping ${srcPath}: ${e.code}`);
          continue;
        }
        throw e;
      }
    }
  };

  await walk(src, dest);
}

export async function runIsolatedOrchestrator(opts: {
  model: string;
  agentType: 'developer' | 'tester' | 'planner' | 'contexter' | 'speccer';
  projectId: string; 
  taskId: string;
  featureId?: string; 
  taskTools: TaskUtils,
  fileTools: FileTools,
  gitFactory: (projectRoot: string) => GitManager;  // git bound to projectRoot
  completion: CompletionClient;
  emit?: (e: { type: string; payload?: any }) => void;
}) {
  const { model, agentType, projectId, taskId, featureId, taskTools, fileTools, gitFactory, completion, emit } = opts;

  const projectDir = await taskTools.getProjectDir(projectId);

  // Create temp workspace
  const tmpBase = fs.mkdtempSync(path.join(os.tmpdir(), 'factory-ts-'));
  // Keep a subfolder to host the copied source
  const workspace = path.join(tmpBase, 'workspace');

  logger.info(`Copying repository from '${projectDir}' to temporary workspace: '${workspace}'`);
  try {
    await copyTree(projectDir, workspace);
  } catch (e) {
    logger.error(`FATAL: Failed to copy repository to temporary directory from '${projectDir}' -> '${workspace}': ${e}`);
    // best-effort cleanup
    try { fs.rmSync(tmpBase, { recursive: true, force: true }); } catch {}
    return;
  }

  const git = gitFactory(workspace);

  try {
    await runOrchestrator({
      model,
      agentType,
      taskId,
      featureId,
      projectDir: workspace,
      taskTools,
      fileTools,
      git,
      completion,
      emit,
    });
  } catch (e) {
    logger.error(`An error occurred while running the agent orchestrator: ${e}`);
  } finally {
    // Cleanup temporary directory
    try {
      fs.rmSync(tmpBase, { recursive: true, force: true });
      logger.info('Agent run finished. Temporary directory cleaned up.');
    } catch (e) {
      logger.warn(`Failed to cleanup temporary directory '${tmpBase}': ${e}`);
    }
  }
}
","src/tools/factory/mainOrchestrator.js":"import { ipcMain, webContents } from 'electron';
import IPC_HANDLER_KEYS from '../../ipcHandlersKeys.js';
import path from 'node:path';
import fs from 'node:fs';

// Load local package factory-ts from monorepo
// Use dynamic import to avoid issues with CJS/ESM resolution in Electron
let factoryTs;
async function loadFactory()
{
  if (factoryTs) return factoryTs;
  const cwd = process.cwd();
  const distEsm = path.resolve(cwd, 'packages/factory-ts/dist/index.js');
  const distCjs = path.resolve(cwd, 'packages/factory-ts/dist/index.cjs');
  try {
    console.log('[factory] Loading factory-ts from', distEsm);
    factoryTs = await import(distEsm);
  } catch (errEsm) {
    console.warn('[factory] Failed to load ESM build, trying CJS', errEsm?.message || errEsm);
    try {
      factoryTs = await import(distCjs);
    } catch (errCjs) {
      console.error('[factory] Failed to load factory-ts from dist. Did you build it? npm run factory:build');
      console.error(errCjs?.stack || String(errCjs));
      throw errCjs;
    }
  }
  console.log('[factory] factory-ts loaded successfully');
  return factoryTs;
}

const RUN_SUBSCRIBERS = new Map(); // runId -> Set<webContentsId>
const RUNS = new Map(); // runId -> RunHandle
const RUN_META = new Map(); // runId -> metadata snapshot
const RUN_TIMERS = new Map(); // runId -> heartbeat timer
const RUN_MESSAGES = new Map(); // runId -> last messages array

let PRICING = null;
let HISTORY = null;

function sendEventToWC(wcId, runId, event) {
  const wc = webContents.fromId(wcId);
  if (wc && !wc.isDestroyed()) {
    try {
      wc.send(IPC_HANDLER_KEYS.FACTORY_EVENT, { runId, event });
    } catch (err) {
      console.warn(`[factory] Failed to send event to wc ${wcId} for run ${runId}:`, err?.message || err);
    }
  }
}

function broadcastEventToSubscribers(runId, event) {
  const subscribers = RUN_SUBSCRIBERS.get(runId);
  if (!subscribers || subscribers.size === 0) return;
  for (const wcId of subscribers) {
    sendEventToWC(wcId, runId, event);
  }
}

function maskSecrets(obj) {
  try {
    const o = JSON.parse(JSON.stringify(obj));
    if (o && o.llmConfig && typeof o.llmConfig === 'object') {
      if ('apiKey' in o.llmConfig) o.llmConfig.apiKey = '***';
    }
    return o;
  } catch {
    return obj;
  }
}

function nowIso() {
  return new Date().toISOString();
}

function persistMeta(runId) {
  try {
    const meta = RUN_META.get(runId);
    if (!meta || !HISTORY) return;
    const h = HISTORY;
    if (typeof h.addOrUpdateRun === 'function') {
      h.addOrUpdateRun(meta);
    } else if (typeof h.updateRunMeta === 'function') {
      h.updateRunMeta(meta.runId, meta);
    } else if (typeof h.upsertRun === 'function') {
      h.upsertRun(meta);
    } else if (typeof h.addRun === 'function') {
      h.addRun(meta);
    } else {
      // No known persistence API; noop
    }
  } catch (e) {
    console.warn('[factory] persistMeta error', e?.message || e);
  }
}

function persistMessages(runId, messages) {
  try {
    if (!HISTORY || !messages) return;
    const h = HISTORY;
    if (typeof h.writeMessages === 'function') {
      h.writeMessages(runId, messages);
    } else if (typeof h.saveMessages === 'function') {
      h.saveMessages(runId, messages);
    }
  } catch (e) {
    console.warn('[factory] persistMessages error', e?.message || e);
  }
}

function updateMetaFromEvent(runId, e) {
  const meta = RUN_META.get(runId);
  if (!meta) return;
  meta.updatedAt = e?.ts || e?.time || nowIso();
  const type = e?.type;
  const payload = e?.payload || {};
  if (type === 'run/progress' || type === 'run/progress/snapshot') {
    if (typeof payload?.progress === 'number') meta.progress = payload.progress;
    else if (typeof payload?.percent === 'number') meta.progress = Math.max(0, Math.min(1, payload.percent / 100));
    if (payload?.message) meta.message = payload.message;
  } else if (type === 'run/usage') {
    if (payload?.costUSD != null) meta.costUSD = payload.costUSD;
    else if (payload?.costUsd != null) meta.costUSD = payload.costUsd;
    else if (payload?.usd != null) meta.costUSD = payload.usd;
    if (payload?.promptTokens != null) meta.promptTokens = payload.promptTokens;
    if (payload?.completionTokens != null) meta.completionTokens = payload.completionTokens;
    if (payload?.provider) meta.provider = payload.provider;
    if (payload?.model) meta.model = payload.model;
  } else if (type === 'run/start') {
    if (payload?.llm?.provider) meta.provider = payload.llm.provider;
    if (payload?.llm?.model) meta.model = payload.llm.model;
  } else if (type === 'run/error') {
    meta.state = 'error';
    meta.message = payload?.message || payload?.error || 'Error';
  } else if (type === 'run/cancelled') {
    meta.state = 'cancelled';
    meta.message = payload?.reason || 'Cancelled';
  } else if (type === 'run/completed' || type === 'run/complete') {
    meta.state = 'completed';
    meta.message = payload?.message || payload?.summary || 'Completed';
  }
  // Persist snapshot on each update
  persistMeta(runId);
}

function loadWorkspaceDotenv(projectRoot) {
  try {
    const dotenv = require('dotenv');
    const envPath = path.join(projectRoot, '.env');
    if (fs.existsSync(envPath)) {
      dotenv.config({ path: envPath });
      console.log('[factory] Loaded workspace .env from', envPath);
    } else {
      console.log('[factory] No .env at', envPath);
    }
  } catch (e) {
    // If dotenv isn't available or fails, ignore silently to avoid crashing Electron main
    console.warn('[factory] dotenv not loaded in main process (optional):', e?.message || e);
  }
}

// --- Normalization: convert persisted messages array into feature-keyed logs ---
const DEFAULT_FEATURE_KEY = '__task__';
function parseFeatureMarker(content) {
  if (!content || typeof content !== 'string') return null;
  // Matches lines like: --- BEGIN FEATURE <id>: <title> ---
  const m = /^---\s*BEGIN\s+FEATURE\s+([^:]+)\s*:/.exec(content);
  return m ? String(m[1]).trim() : null;
}
function ensureGroup(map, featureId) {
  if (!map[featureId]) {
    map[featureId] = { startDate: new Date().toISOString(), featureId, messages: [] };
  }
  return map[featureId];
}
function normalizeRunMessagesToFeatureLog(messages) {
  // If already in the expected shape (object of { featureId: { startDate, messages } }), return as-is
  if (messages && !Array.isArray(messages) && typeof messages === 'object') {
    return messages;
  }
  const result = {};
  if (!Array.isArray(messages)) {
    // Unknown shape -> fallback to empty task bucket
    result[DEFAULT_FEATURE_KEY] = { startDate: new Date().toISOString(), featureId: DEFAULT_FEATURE_KEY, messages: [] };
    return result;
  }
  let current = DEFAULT_FEATURE_KEY;
  ensureGroup(result, current);
  for (const msg of messages) {
    try {
      const role = msg?.role;
      const content = msg?.content;
      if (role === 'system') {
        const fid = parseFeatureMarker(content);
        if (fid) {
          current = fid;
          ensureGroup(result, current);
          // Do not include the marker message in the feature messages
          continue;
        }
      }
      const grp = ensureGroup(result, current);
      // Push shallow clone to avoid accidental mutations downstream
      grp.messages.push({ ...msg });
    } catch {}
  }
  // Assign a simple endDate for groups that have messages
  const now = new Date().toISOString();
  for (const fid of Object.keys(result)) {
    const g = result[fid];
    if (g && Array.isArray(g.messages) && g.messages.length > 0) {
      g.endDate = now;
    }
  }
  return result;
}

export async function registerFactoryIPC(mainWindow, projectRoot) {
  console.log('[factory] Registering IPC handlers. projectRoot=', projectRoot);

  // Ensure workspace .env is loaded in main process so child processes and libs see credentials
  loadWorkspaceDotenv(projectRoot);

  const factory = await loadFactory();
  const { createOrchestrator, createHistoryStore, createPricingManager } = factory

  try {
    const dbPath = path.join(projectRoot, '.factory', 'history.sqlite');
    if (typeof createHistoryStore === 'function') {
      console.log('[factory] Initializing history store at', dbPath);
      HISTORY = createHistoryStore({ dbPath });
    } else {
      console.log('[factory] History store not available; proceeding without persistence');
    }
  } catch (err) {
