}

function UserBubble({ title, text }: { title?: string; text: string }) {
  return (
    <div className="max-w-[80%] rounded-2xl px-3 py-2 bg-neutral-100 text-neutral-900 dark:bg-neutral-800 dark:text-neutral-50 shadow-sm">
      {title ? <div className="text-[11px] font-medium mb-1">{title}</div> : null}
      {isLargeText(text) ? (
        <ScrollableTextBox text={text} />
      ) : (
        <div className="text-xs whitespace-pre-wrap break-words"><RichText text={text} /></div>
      )}
    </div>
  );
}

function FeatureContent({ log }: { log: AgentFeatureRunLog }) {
  // Recompute on every render to reflect in-place mutations of log.messages
  const { initial, turns } = buildFeatureTurns(log.messages || []);
  const lastTurnRef = useRef<HTMLDivElement | null>(null);

  return (
    <div className="space-y-2 p-1">
      {initial ? (
        <Collapsible title={<span className="flex items-center1">Initial prompt</span>} defaultOpen>
          {isLargeText(initial.content || '') ? (
            <ScrollableTextBox text={initial.content || ''} />
          ) : (
            <div className="p-2 text-xs whitespace-pre-wrap break-words"><RichText text={initial.content || ''} /></div>
          )}
        </Collapsible>
      ) : (
        <div className="text-sm text-neutral-500">No conversation yet.</div>
      )}

      {turns.map((t, idx) => {
        const parsed = parseAssistant(t.assistant?.content || '');
        const toolCalls: ToolCall[] = parsed?.tool_calls || [];
        const resultsObjs = parseToolResultsObjects(t.tools);
        const pickResultForCall = (call: ToolCall, i: number) => resultsObjs[i]?.result ?? undefined;
        const hasThoughts = parsed?.thoughts && parsed.thoughts.trim().length > 0;
        const isFinal = t.isFinal || (toolCalls.length === 0);

        return (
          <div key={idx} ref={idx === turns.length - 1 ? lastTurnRef : undefined}>
            <Collapsible innerClassName='p-2' title={<span className="flex items-center gap-2">{isFinal ? 'Final' : `Turn ${idx + 1}`}</span>} defaultOpen={idx === turns.length - 1}>
              <div className="space-y-2">
                <div className="flex items-start justify-between gap-2">
                  <div className="min-w-0">
                    {hasThoughts ? (
                      <AssistantBubble text={parsed!.thoughts!} />
                    ) : (
                      <AssistantBubble title="Assistant" text={t.assistant?.content || ''} />
                    )}
                  </div>
                  {t.thinkingTime != null && (
                    <div className="flex-shrink-0 bg-neutral-100 dark:bg-neutral-800 rounded-full px-2 py-0.5 text-xs text-neutral-500 dark:text-neutral-400 whitespace-nowrap mt-1">
                      {(t.thinkingTime / 1000).toFixed(1)}s
                    </div>
                  )}
                </div>

                {toolCalls.length > 0 ? (
                  <div className="space-y-2">
                    {toolCalls.map((call, i) => (
                      <ToolCallRow key={i} call={call} index={i} resultText={pickResultForCall(call, i)} />
                    ))}
                  </div>
                ) : null}

                {toolCalls.length === 0 && !isFinal && resultsObjs.length > 0 ? (
                  <div className="rounded-md border border-neutral-200 dark:border-neutral-800 bg-neutral-50 dark:bg-neutral-900/40 p-2">
                    <div className="text-[11px] text-neutral-600 dark:text-neutral-400 mb-1">Tool results</div>
                    {resultsObjs.map((r, i) => (
                      <div key={i} className="rounded bg-white dark:bg-neutral-950 border border-neutral-200 dark:border-neutral-800 p-2 mb-2 last:mb-0 text-xs whitespace-pre-wrap break-words max-h-60 overflow-auto">
                        <SafeText text={r.result} />
                      </div>
                    ))}
                  </div>
                ) : null}
              </div>
            </Collapsible>
          </div>
        );
      })}
    </div>
  );
}

export default function ChatConversation({ run }: { run: AgentRun }) {
  // Derive logs (sorted by startDate)
  const logs = useMemo(() => {
    const SKIP_FEATURE_KEYS = new Set(['__task__', '_task']);
    const list = Object.values(run.messagesLog ?? {}).filter((l) => !SKIP_FEATURE_KEYS.has((l as any).featureId));
    return list.sort((a, b) => {
      const at = new Date((a as any).startDate).getTime();
      const bt = new Date((b as any).startDate).getTime();
      return at - bt;
    });
  }, [run]);

  // Track a scroll container and auto-scroll behavior
  const containerRef = useRef<HTMLUListElement | null>(null);
  const stickToBottomRef = useRef(true);

  // Update stickToBottomRef on user scroll
  useEffect(() => {
    const el = containerRef.current;
    if (!el) return;
    const onScroll = () => {
      const threshold = 40; // px
      const atBottom = el.scrollTop + el.clientHeight >= el.scrollHeight - threshold;
      stickToBottomRef.current = atBottom;
    };
    el.addEventListener('scroll', onScroll, { passive: true } as any);
    // Initialize state
    onScroll();
    return () => el.removeEventListener('scroll', onScroll);
  }, []);

  // Compute a simple metric to detect new content
  const contentSize = useMemo(() => {
    let count = 0;
    for (const l of logs) count += (l.messages?.length || 0);
    return count + ':' + logs.length;
  }, [logs]);

  // Auto-scroll when new content arrives and user is at bottom
  useLayoutEffect(() => {
    const el = containerRef.current;
    if (!el) return;
    if (stickToBottomRef.current) {
      el.scrollTo({ top: el.scrollHeight });
    }
  }, [contentSize]);

  return (
    <ul ref={containerRef} className="h-[60vh] max-h-[70vh] overflow-auto bg-neutral-50 dark:bg-neutral-900 rounded-md border border-neutral-200 dark:border-neutral-800 p-3 space-y-3" role="log" aria-live="polite">
      {logs.length === 0 ? (
        <div className="text-sm text-neutral-500">No features messages to display.</div>
      ) : (
        <>{logs.map((log) => {
          const start = log.startDate ? new Date(log.startDate as any) : undefined;
          const end = log.endDate ? new Date(log.endDate as any) : undefined;
          const subtitle = [start ? start.toLocaleString() : null, end ? ` ${end.toLocaleString()}` : null].filter(Boolean).join(' ');
          return (
            <li key={log.featureId}>
              <Collapsible title={<span className="flex items-center">Feature: {log.featureId}{subtitle ? <span className="text-neutral-500 text-[11px] px-3 py-2"> {subtitle}</span> : null}</span>} defaultOpen>
                <FeatureContent log={log} />
              </Collapsible>
            </li>
          );
        })}</>
      )}
    </ul>
  );
}
","src/renderer/services/agentsService.ts":"import { EventSourceLike, attachToRun, startTaskRun, startFeatureRun, deleteHistoryRun } from '../../tools/factory/orchestratorBridge';
import { LLMConfigManager } from '../utils/LLMConfigManager';
import { AgentType, LLMConfig } from 'packages/factory-ts/src/types';
import { notificationsService } from './notificationsService';

export type AgentRunState = 'running' | 'completed' | 'cancelled' | 'error';

export interface AgentRunMessage {
  role: string;
  content: string;
}

export type AgentRun = {
  runId: string;
  agentType: AgentType;
  projectId: string;
  taskId: string;
  state: AgentRunState;
  message?: string;
  progress?: number; // 0..1 (if available)
  costUSD?: number;
  promptTokens?: number;
  completionTokens?: number;
  provider?: string;
  model?: string;
  startedAt: string; // ISO
  updatedAt: string; // ISO
  // Timestamp of the last received LLM message (assistant or tool results). Used for thinking timer.
  lastMessageAt?: string; // ISO

  messagesLog?: Record<string,AgentFeatureRunLog>
};
export type AgentFeatureRunLog = {
  startDate: Date;
  endDate?: Date;
  featureId: string;
  messages: AgentRunMessage[];
}

// Internal structure to keep handle + eventSource
interface RunRecord extends AgentRun {
  events: EventSourceLike;
  cancel: (reason?: string) => void;
  // Track which featureIds we've already notified as completed for this run
  __notifiedFeatures?: Set<string>;
}

type Subscriber = (runs: AgentRun[]) => void;

function getEventTs(e: any): string {
  return e?.ts || e?.time || new Date().toISOString();
}

function getCostUSD(payload: any | undefined): number | undefined {
  if (!payload) return undefined;
  return payload.costUSD ?? payload.costUsd ?? payload.usd ?? undefined;
}

function redactConfig(config: LLMConfig | null | undefined) {
  if (!config) return null;
  const { apiKey, ...rest } = config as any;
  return { ...rest, apiKey: apiKey ? '***' : '' };
}

const NOOP_EVENTS: EventSourceLike = { addEventListener: (_t: string, _h: (e: any) => void) => {}, close: () => {} };
const DEFAULT_FEATURE_KEY = '__task__';

class AgentsServiceImpl {
  private runs = new Map<string, RunRecord>();
  private subscribers = new Set<Subscriber>();
  private llmManager = new LLMConfigManager();
  private bootstrapped = false;

  private notify() {
    const list = Array.from(this.runs.values()).map(this.publicFromRecord);
    for (const cb of this.subscribers) cb(list);
  }

  private publicFromRecord(rec: RunRecord): AgentRun {
    const { events: _ev, cancel: _c, __notifiedFeatures: _nf, ...pub } = rec as any;
    return pub as AgentRun;
  }

  private async bootstrapFromActiveRuns() {
    if (this.bootstrapped) return;
    this.bootstrapped = true;
    try {
      const factory = (window as any).factory;
      if (!factory) return;
      // 1) Attach to active
      if (typeof factory.listActiveRuns === 'function') {
        const active = await factory.listActiveRuns();
        if (Array.isArray(active)) {
          for (const m of active) {
            if (!m?.runId) continue;
            if (this.runs.has(m.runId)) continue;
            const { handle, events } = attachToRun(m.runId);
            const run: RunRecord = {
              runId: handle.id,
              agentType: m.agentType,
              projectId: m.projectId,
              taskId: m.taskId,
              featureId: m.featureId ?? undefined,
              state: m.state ?? 'running',
              message: m.message ?? 'Running... ',
              progress: m.progress,
              costUSD: m.costUSD,
              promptTokens: m.promptTokens,
              completionTokens: m.completionTokens,
              provider: m.provider,
              model: m.model,
              startedAt: m.startedAt || new Date().toISOString(),
              updatedAt: m.updatedAt || new Date().toISOString(),
              lastMessageAt: undefined,
              messagesLog: {},
              events,
              cancel: (reason?: string) => handle.cancel(reason),
              __notifiedFeatures: new Set<string>(),
            } as RunRecord;
            this.wireRunEvents(run);
            this.runs.set(run.runId, run);

            try {
              run.messagesLog  = await factory.getRunMessages(run.runId);
            } catch (err) {
              console.warn('[agentsService] Failed to load messages for active run', run.runId, (err as any)?.message || err);
            }
          }
        }
      }

      const history = await factory.listRunHistory();
      if (Array.isArray(history)) {
        for (const m of history) {
          if (!m?.runId) continue;
          if (this.runs.has(m.runId)) continue;
          const rec: RunRecord = {
            runId: m.runId,
            agentType: m.agentType,
            projectId: m.projectId,
            taskId: m.taskId,
            featureId: m.featureId ?? undefined,
            state: m.state ?? 'completed',
            message: m.message ?? '',
            progress: m.progress,
            costUSD: m.costUSD,
            promptTokens: m.promptTokens,
            completionTokens: m.completionTokens,
