          {filtered.map((cmd, i) => {
            const active = i === selectedIndex;
            return (
              <li
                key={cmd.id}
                id={`${listboxId}-option-${i}`}
                className={`cmd__item${active ? ' is-active' : ''}`}
                role="option"
                aria-selected={active}
                onMouseEnter={() => setSelectedIndex(i)}
                ref={(el) => (itemRefs.current[i] = el)}
