            </SelectContent>
          </Select>
        </div>
      </div>
    </div>
  );

  return (
    <CollapsibleSidebar
      items={CATEGORIES}
      activeId={activeCategory}
      onSelect={(c) => { setActiveCategory(c as CategoryId)}}
      storageKey="settings-panel-collapsed"
