                {/* Auto-update toggle and trigger */}
                <div className="flex flex-col gap-2">
                  <Switch
                    checked={!!s.autoUpdate?.enabled}
                    onCheckedChange={(checked) =>
                      updateConfig(s.id, { autoUpdate: { ...(s.autoUpdate || { trigger: 'onAppLaunch' }), enabled: checked } })
