        if (String(t).startsWith('llm/')) {
          console.log('[factory] event', runHandle.id, t, e?.payload ? JSON.parse(JSON.stringify(e.payload)) : undefined);
        } else if (t === 'run/log' || t === 'run/progress' || t === 'run/progress/snapshot') {
