        }
      } catch {}

      // Capture and persist messages when provided (init, snapshots, or final)
      try {
        if ((e?.type === 'llm/messages/snapshot' || e?.type === 'llm/messages/final' || e?.type === 'llm/messages/init') && (e?.payload?.messages || e?.payload?.messageGroups)) {
