    const list = [];
    for (const [runId, meta] of RUN_META.entries()) {
      if (meta?.state === 'running') {
        list.push({ ...meta, runId });
      }
    }
    return list;
  });

  // History listing and messages API
  ipcMain.handle(IPC_HANDLER_KEYS.FACTORY_HISTORY_LIST, () => {
    try {
      return HISTORY?.listRuns?.() || [];
    } catch (e) {
