import { findNextAvailableFeature } from './workflowUtils.js';

const MAX_TURNS_PER_FEATURE = 100;

const NEWLINE = '\n';

const PROTOCOL_INSTRUCTIONS = (() => {
  return [
    'You **MUST** respond in a single, valid JSON object. This object must adhere to the following structure:',
    '```json',
    agent_response_example,
    '```',
    'The thoughts field is for your reasoning, and tool_calls is a list of actions to execute.',
    'Your response will be parsed as JSON. Do not include any text outside of this JSON object.',
    'As responses to the tool calls you will send, you will also receive JSON objects in the form:',
    '```json',
    '{\n"name":"tool_name",\n"result":"..."\n}',
    '```',
  ].join('\n');
})();

// Human-readable descriptions for tools to avoid duplicating them in agent docs
const TOOL_DESCRIPTIONS: Record<string, string> = {
  // Base tools
  read_files: 'Read the content of one or more files at the given relative paths. Returns an array of strings (one per file).',
  search_files: 'Search for files by name or textual content under the given path. Returns matching relative paths. The query input must be a space separated list of keywords',
  list_files: 'List files and directories at a relative path from the project root.',
  finish_feature: 'Mark the current feature as complete and end the run. Use only when the work fully meets the acceptance criteria.',
  block_feature: 'Stop work on the current feature and report why you are blocked (e.g., missing context, unclear requirements).',

  // Speccer
  create_feature: 'Define and add a new, atomic feature to the task. Include a concise title and a clear description.',
  finish_spec: 'Signal that the feature specification is complete for the current task.',
  block_task: 'Stop work on the task and report why you are blocked at the task level.',

  // Developer
  write_file: 'Create or overwrite a file with the given content. Always use this to write code or docs.',
  rename_file: 'Rename or move a file from one path to another.',
  delete_file: 'Delete a file. Use with caution and only when necessary.',
  run_test: 'Run the test for the current feature and return the output. Use to verify implementation.',

  // Planner
  update_feature_plan: 'Save a concise, step-by-step implementation plan for the assigned feature.',

  // Tester
  update_acceptance_criteria: 'Save a clear, verifiable list of acceptance criteria for the assigned feature.',
  update_test: 'Save the Python test script that validates the acceptance criteria for this feature.',

  // Contexter
  update_feature_context: 'Save the minimal set of file paths (relative to repo root) required to implement the feature.'
};

function toolSigsForAgent(agent: string, tools: Tools, git: GitManager): [Record<string, (args: any) => Promise<any> | any>, string[]] {
  const base: Record<string, [(args: any) => Promise<any> | any, string]> = {
    read_files: [ (args: { paths: string[] }) => tools.readFiles(args.paths), "read_files(paths: list[str]) -> list[str]" ],
    search_files: [ (args: { query: string; path?: string }) => tools.searchFiles(args.query, args.path ?? '.'), "search_files(query: str, path: str = '.') -> list[str]" ],
    block_feature: [ (args: { task_id: string; feature_id: string; reason: string }) => tools.blockFeature?.(args.task_id, args.feature_id, args.reason, agent, git), 'block_feature(reason: str)' ],
    finish_feature: [ (args: { task_id: string; feature_id: string }) => tools.finishFeature?.(args.task_id, args.feature_id, agent, git), 'finish_feature()' ],
    list_files: [ (args: { path: string }) => tools.listFiles(args.path), 'list_files(path: str)' ],
  };

  const agentTools: Record<string, [(args: any) => Promise<any> | any, string]> = {};

  if (agent === 'speccer') {
    agentTools.create_feature = [ (args: { task_id: string; title: string; description: string }) => tools.createFeature(args.task_id, args.title, args.description), 'create_feature(title: str, description: str)' ];
    agentTools.finish_spec = [ (args: { task_id: string }) => tools.finishSpec(args.task_id, agent, git), 'finish_spec()' ];
    agentTools.block_task = [ (args: { task_id: string; reason: string }) => tools.blockTask(args.task_id, args.reason, agent, git), 'block_task()' ];
  } else if (agent === 'developer') {
    agentTools.write_file = [ (args: { filename: string; content: string }) => tools.writeFile(args.filename, args.content), 'write_file(filename: str, content: str)' ];
    agentTools.rename_file = [ (args: { filename: string; new_filename: string }) => tools.renameFile(args.filename, args.new_filename), 'rename_file(filename: str, new_filename: str)' ];
    agentTools.delete_file = [ (args: { filename: string }) => tools.deleteFile(args.filename), 'delete_file(filename: str)' ];
    agentTools.run_test = [ (args: { task_id: string; feature_id: string }) => tools.runTest(args.task_id, args.feature_id), 'run_test() -> str' ];
  } else if (agent === 'planner') {
    agentTools.update_feature_plan = [ (args: { task_id: string; feature_id: string; plan: any }) => tools.updateFeaturePlan(args.task_id, args.feature_id, args.plan), 'update_feature_plan(plan: str)' ];
  } else if (agent === 'tester') {
    agentTools.update_acceptance_criteria = [ (args: { task_id: string; feature_id: string; criteria: string }) => tools.updateFeatureAcceptance(args.task_id, args.feature_id, args.criteria), 'update_acceptance_criteria(criteria: str)' ];
    agentTools.update_test = [ (args: { task_id: string; feature_id: string; test: string }) => tools.updateTest(args.task_id, args.feature_id, args.test), 'update_test(test: str)' ];
    agentTools.run_test = [ (args: { task_id: string; feature_id: string }) => tools.runTest(args.task_id, args.feature_id), 'run_test() -> str' ];
  } else if (agent === 'contexter') {
    agentTools.update_feature_context = [ (args: { task_id: string; feature_id: string; context: string[] }) => tools.updateFeatureContext(args.task_id, args.feature_id, args.context), 'update_feature_context(context: list[str])' ];
  }

  const merged = { ...base, ...agentTools };
  const funcs: Record<string, (args: any) => Promise<any> | any> = {};
  const sigs: string[] = [];
  for (const [name, [fn, sig]] of Object.entries(merged)) {
    if (fn) {
      funcs[name] = fn;
      const desc = TOOL_DESCRIPTIONS[name] ? ` - ${TOOL_DESCRIPTIONS[name]}` : '';
      sigs.push(`${sig}${desc}`);
    }
  }
  return [funcs, sigs];
}

function constructSystemPrompt(agent: string, task: Task, feature: Feature | null, agentSystemPrompt: string, context: string, toolSignatures: string[]): string {
  const plan = feature && (agent === 'developer' || agent === 'tester' || agent === 'contexter' || agent === 'planner') ? feature.plan : undefined;
  const acceptance = feature && (agent === 'developer' || agent === 'tester') ? feature.acceptance : undefined
  const toolSignaturesStr = toolSignatures.map(sig => `- ${sig}`).join('\n');

  const featureBlock = feature ? `#ASSIGNED FEATURE: ${feature.title} (ID: ${feature.id})${NEWLINE}##DESCRIPTION: ${feature.description}${NEWLINE}` : '';
  const featureRejection = feature?.rejection ? `##REJECTION REASON:${NEWLINE}${feature.rejection}${NEWLINE}` : '';
  const taskRejection = task.rejection ? `##REJECTION REASON:${NEWLINE}${task.rejection}` : '';

  return [
    agentSystemPrompt,
    `#CURRENT TASK (ID: ${task.id})`,
    '##TITLE:',
    task.title,
    '##DESCRIPTION:',
    task.description,
    taskRejection,
    '',
    featureBlock,
    featureRejection,
    plan ? '#THE PLAN' + plan : '',
    '',
    acceptance ? '#ACCEPTANCE CRITERIA:' + acceptance : '',
    '',
    '#TOOL SIGNATURES:',
    `${toolSignaturesStr}`,
    '',
    '#RESPONSE FORMAT INSTRUCTIONS:',
    PROTOCOL_INSTRUCTIONS,
    '',
    '#CONTEXT FILES PROVIDED:',
    context,
    '',
    'Begin now.'
  ].join('\n');
}

// Attach metadata to messages so history can be reconstructed reliably after persistence
function annotateMessage<T extends CompletionMessage>(msg: T, meta: { featureId?: string; agentType?: string; turn?: number; source?: string; marker?: string; askedAt?: string }): T {
  const anyMsg = msg as any;
  if (meta.featureId) anyMsg.featureId = meta.featureId;
  if (meta.agentType) anyMsg.agentType = meta.agentType;
  if (typeof meta.turn === 'number') anyMsg.turn = meta.turn;
  if (meta.source) anyMsg.source = meta.source;
  if (meta.marker) anyMsg.marker = meta.marker;
  if (meta.askedAt) anyMsg.askedAt = meta.askedAt;
  anyMsg.createdAt = new Date().toISOString();
  return msg;
}

// Grouped history structure for persistence/readability
type FeatureMessagesGroup = { startDate: string; endDate?: string; featureId: string; messages: CompletionMessage[] };

function ensureGroup(groups: Record<string, FeatureMessagesGroup>, featureId: string): FeatureMessagesGroup {
  if (!groups[featureId]) {
    groups[featureId] = { featureId, startDate: new Date().toISOString(), messages: [] };
  }
  return groups[featureId];
}

function parseAgentResponse(content: string) : AgentResponse | undefined
{
  let parsed: MatchAgentResponse | undefined;
  try { parsed = JSON.parse(content) as MatchAgentResponse; } catch {}

  if (!parsed) { return }
  const thoughts = parsed.thoughts
  const toolCallsMatched: MatchToolCall[] = parsed.tool_calls ?? parsed.tools ?? parsed.calls ?? []; 

  let tool_calls : ToolCall[] = []
  for(const t of toolCallsMatched){
    tool_calls.push({
      tool_name: t.tool_name ?? t.name ?? t.tool ?? "",
      arguments: t.arguments ?? t.args ?? t.parameters ?? t.params,
    })
  }
  return { thoughts, tool_calls }
}

const toolArgMappings: Record<string, Record<string, string[]>> = {
  read_files: { paths: ['files', 'in', 'path'] },
  search_files: { query: ['q', 'search'], path: ['in', 'dir', 'directory'] },
  block_feature: { reason: ['cause', 'explanation'] },
  list_files: { path: ['in', 'dir', 'directory'] },
  create_feature: { title: ['name'], description: ['desc', 'details'] },
  block_task: { reason: ['cause', 'explanation'] },
  write_file: { filename: ['path', 'file', 'name'], content: ['text', 'data'] },
  rename_file: { filename: ['from', 'source', 'src', 'old_filename'], new_filename: ['to', 'destination', 'dest', 'new_name'] },
  delete_file: { filename: ['path', 'file', 'name'] },
  update_feature_plan: { plan: ['steps'] },
  update_acceptance_criteria: { criteria: ['acceptance', 'acceptance_criteria'] },
  update_test: { test: ['code', 'script'] },
  update_feature_context: { context: ['files', 'paths'] }
};

async function runConversation(opts: {
  model: string;
  availableTools: Record<string, (args: any) => Promise<any> | any>;
  systemPrompt: string;
  task: Task;
  feature: Feature | null;
  agentType: string;
  toolFunctions: Tools;
  git: GitManager;
  completion: CompletionClient;
  complete: (why: 'finish' | 'block' | 'max_turns', info?: any) => void;
  emit?: (e: { type: string; payload?: any }) => void; // event sink for logging/stats
}) {
  const { model, availableTools, systemPrompt, task, feature, agentType, toolFunctions, git, completion, complete, emit } = opts;
  const doEmit = (e: { type: string; payload?: any }) => { try { emit?.(e); } catch {} };

  const featureKey = feature?.id ?? '__task__';

  const initialUser: CompletionMessage = annotateMessage(
    { role: 'user', content: systemPrompt } as any,
    { featureId: featureKey, agentType, turn: 0, source: 'initial' }
  );
  const messages: CompletionMessage[] = [initialUser];
  doEmit({ type: 'llm/messages/init', payload: { messages: messages.slice(), featureId: featureKey, turn: 0 } });

  for (let i = 0; i < MAX_TURNS_PER_FEATURE; i++) {
    try {
      // Emit snapshot of request context before calling the LLM
      doEmit({ type: 'llm/messages/snapshot', payload: { messages: messages.slice(), featureId: featureKey, turn: i } });
      const startedAt = Date.now();
      const res = await completion({ model, messages, response_format: { type: 'json_object' } });
      const durationMs = Date.now() - startedAt;

      const agentResponse = parseAgentResponse(res.message.content)

      const assistant = annotateMessage(
        { role: 'assistant' as const, content: JSON.stringify(agentResponse) ?? res.message.content },
        { featureId: featureKey, agentType, turn: i, askedAt: new Date(startedAt).toISOString() }
      );
      messages.push(assistant);
      // Emit assistant message and timing
      doEmit({ type: 'llm/message', payload: { message: assistant, featureId: featureKey, durationMs } });

      // Parse and log succinct info
      logLLMStep({ turn: i, thoughts: agentResponse?.thoughts, toolCalls: agentResponse?.tool_calls, durationMs, tag: agentType });

      if (!agentResponse?.tool_calls?.length) {
        // No tool calls; continue loop.
        doEmit({ type: 'llm/messages/snapshot', payload: { messages: messages.slice(), featureId: featureKey, turn: i, note: 'no_tool_calls' } });
        doEmit({ type: 'run/progress/snapshot', payload: { progress: Math.min(0.99, (i + 1) / MAX_TURNS_PER_FEATURE), message: `Turn ${i} complete` } });
        continue;
      }

      const toolOutputs: ToolResult[] = [];

      for (const call of agentResponse.tool_calls) {
        const toolName = call.tool_name;
        let args = call.arguments;

        const mapping = toolArgMappings[toolName];
        if (mapping) {
          args = normalizeToolArguments(args, mapping);
        }

        if (availableTools[toolName]) {
          if (!('task_id' in args)) args.task_id = task.id;
          if (feature && !('feature_id' in args)) args.feature_id = feature.id;
          const result = await Promise.resolve(availableTools[toolName](args));
          toolOutputs.push({ name: toolName, result });
        } else {
          toolOutputs.push({ name: toolName, result: `Error: Tool '${toolName}' not found.` });
        }

        // Termination tools mirror Python; do not append a tool result turn, final message is assistant
        if (['finish_feature', 'block_feature', 'finish_spec', 'block_task'].includes(toolName)) {
          doEmit({ type: 'llm/messages/final', payload: { messages: messages.slice(), featureId: featureKey, tool: toolName } });
          complete(toolName === 'block_feature' || toolName === 'block_task' ? 'block' : 'finish');
          return;
        }
      }

      // Append tool results as a dedicated 'user' role message to avoid confusing with user turns
      const toolResultMsg: CompletionMessage = annotateMessage(
        { role: 'user', content: JSON.stringify(toolOutputs) } as any,
        { featureId: featureKey, agentType, turn: i, source: 'tools' }
      );
      messages.push(toolResultMsg);
      doEmit({ type: 'llm/message', payload: { message: toolResultMsg, featureId: featureKey, turn: i, source: 'tools' } });
      doEmit({ type: 'llm/messages/snapshot', payload: { messages: messages.slice(), featureId: featureKey, turn: i, note: 'tools_appended' } });
      doEmit({ type: 'run/progress/snapshot', payload: { progress: Math.min(0.99, (i + 1) / MAX_TURNS_PER_FEATURE), message: `Turn ${i} tool calls processed` } });
    } catch (e) {
      if (feature) {
        await toolFunctions.blockFeature(task.id, feature.id, `Agent loop failed: ${e}`, agentType, git);
      } else {
        await toolFunctions.blockTask(task.id, `Agent loop failed: ${e}`, agentType, git);
      }
      doEmit({ type: 'llm/messages/error', payload: { error: String(e), messages: messages.slice(), featureId: featureKey } });
      doEmit({ type: 'run/progress/snapshot', payload: { progress: undefined, message: 'Error encountered' } });
      logger.error('Agent loop error:', e as any);
      complete('block', e);
      return;
    }
  }

  // Max turns reached -> block
  if (feature) {
    await toolFunctions.blockFeature(task.id, feature.id, 'Agent loop exceeded max turns', agentType, git);
  } else {
    await toolFunctions.blockTask(task.id, 'Agent loop exceeded max turns', agentType, git);
  }
  doEmit({ type: 'llm/messages/final', payload: { messages: [{ role: 'system', content: systemPrompt }] as CompletionMessage[], featureId: featureKey, reason: 'max_turns' } });
  doEmit({ type: 'run/progress/snapshot', payload: { progress: 1, message: 'Max turns reached' } });
  logger.warn('Max turns reached without completion.');
  complete('max_turns');
}

async function runAgentOnTask(model: string, agentType: AgentType, task: Task, toolFunctions: Tools, git: GitManager, completion: CompletionClient, emit?: (e: { type: string; payload?: any }) => void) {
  logger.debug(`\n--- Activating Agent ${agentType} for task: [${task.id}] ${task.title} ---`);
  const agentDocs = getAgentFile(agentType);
  const contextFiles = ['docs/FILE_ORGANISATION.md'];
  const [funcs, sigs] = toolSigsForAgent(agentType, toolFunctions, git);
  const context = await Promise.resolve(toolFunctions.readFiles(contextFiles));
  const systemPrompt = constructSystemPrompt(agentType, task, null, agentDocs, context, sigs);

  // Maintain grouped structure for task-level chat
  const groups: Record<string, FeatureMessagesGroup> = {};
  const fid = '__task__';
  ensureGroup(groups, fid);

  await runConversation({
    model,
    availableTools: funcs,
    systemPrompt,
    task,
    feature: null,
    agentType,
    toolFunctions,
    git,
    completion,
    complete: () => {},
    emit: (e) => {
      // Pass-through event, but attach grouped messages snapshot when available
      try {
        if (e?.payload?.messages && Array.isArray(e.payload.messages)) {
          // Update group messages with annotated copies
          const msgs = (e.payload.messages as CompletionMessage[]).map((m, idx) => annotateMessage(m, { featureId: (m as any).featureId ?? fid, agentType, turn: (m as any).turn ?? idx }));
          groups[fid].messages = msgs;
          if (e.type === 'llm/messages/final') groups[fid].endDate = new Date().toISOString();
          emit?.({ ...e, payload: { ...e.payload, messageGroups: { ...groups }, featureId: fid } });
          return;
        }
      } catch {}
      emit?.(e);
    },
  });
}

async function runAgentOnFeature(model: string, agentType: AgentType, task: Task, feature: Feature, toolFunctions: Tools, git: GitManager, completion: CompletionClient, emit?: (e: { type: string; payload?: any }) => void) {
  logger.debug(`\n--- Activating Agent ${agentType} for Feature: [${feature.id}] ${feature.title} ---`);

  if (agentType === 'developer') await toolFunctions.updateFeatureStatus(task.id, feature.id, '~');

  const agentDocs = getAgentFile(agentType);
  const featureContextFiles = ['docs/FILE_ORGANISATION.md', ...(feature.context ?? [])];
  const [funcs, sigs] = toolSigsForAgent(agentType, toolFunctions, git);
  const context = await Promise.resolve(toolFunctions.readFiles(featureContextFiles));
  const systemPrompt = constructSystemPrompt(agentType, task, feature, agentDocs, context, sigs);

  if (agentType === 'developer') await toolFunctions.updateFeatureStatus(task.id, feature.id, '~');

  await runConversation({
    model,
    availableTools: funcs,
    systemPrompt,
    task,
    feature,
    agentType,
    toolFunctions,
    git,
    completion,
    complete: () => {},
    emit,
  });
}

async function runOrchestrator(opts: {
  model: string;
  agentType: AgentType;
  taskId: string; 
  featureId?: string; 
  tools: Tools;
  git: GitManager;
  completion: CompletionClient;
  emit?: (e: { type: string; payload?: any }) => void;
}) {
  const { model, agentType, taskId, tools, git, completion, emit } = opts;

  const currentTask: Task = await tools.getTask(taskId);
  if (!currentTask) {
    logger.warn('No available tasks to work on in the repository.');
    return;
  }

  logger.debug(`Selected Task: [${currentTask.id}] ${currentTask.title}`);

  const branch = `features/${currentTask.id}`;
  try {
    await git.checkoutBranch(branch, true);
  } catch (e) {
    logger.warn(`Could not create or checkout branch '${branch}': ${e}`);
    await git.checkoutBranch(branch, false);
  }
  try {
    await git.pull(branch);
  } catch (e) {
    logger.warn(`Could not pull branch '${branch}': ${e}`);
  }

  const processed = new Set<string>();

  // Aggregate all messages across features for this run
  let aggregatedMessages: CompletionMessage[] = [];
  const aggregatedGroups: Record<string, FeatureMessagesGroup> = {};

  if (agentType === 'speccer') {
    const freshTask = await tools.getTask(currentTask.id);
    await runAgentOnTask(model, agentType, freshTask, tools, git, completion, (e) => {
      // For task-level runs, forward events and add grouped snapshot for persistence
      if (e?.payload?.messages) {
        const msgs = e.payload.messages as CompletionMessage[];
        const fid = '__task__';
        const annotated = msgs.map((m, idx) => annotateMessage(m, { featureId: (m as any).featureId ?? fid, agentType, turn: (m as any).turn ?? idx }));
        aggregatedMessages = annotated.slice();
        const group = ensureGroup(aggregatedGroups, fid);
        group.messages = annotated.slice();
        if (e.type === 'llm/messages/final') group.endDate = new Date().toISOString();
        emit?.({ ...e, payload: { ...e.payload, featureId: fid, messages: aggregatedMessages.slice(), messageGroups: { ...aggregatedGroups } } });
      } else {
        emit?.(e);
      }
    });
    return;
  }

  while (true) {
    const freshTask = await tools.getTask(currentTask.id);
    const next = findNextAvailableFeature(freshTask, processed, agentType !== 'developer');
    if (!next) {
      logger.debug(`\nNo more available features for task ${freshTask.id}.`);
      break;
    }

    // Ensure a group exists for this feature
    ensureGroup(aggregatedGroups, next.id);

    // Insert a system marker before each feature to clearly separate in UI and history (flat array only)
    const featureMarker: CompletionMessage = annotateMessage(
      { role: 'system', content: `--- BEGIN FEATURE ${next.id}: ${next.title} ---` } as any,
      { featureId: next.id, agentType, marker: 'feature_begin' }
    );
    aggregatedMessages.push(featureMarker);
    emit?.({ type: 'llm/messages/snapshot', payload: { messages: aggregatedMessages.slice(), messageGroups: { ...aggregatedGroups }, note: 'feature_begin', featureId: next.id } });

    // Wrap emit to merge current feature's messages with the aggregate so far
    let lastFeatureMessages: CompletionMessage[] = [];
    const baseEmit = emit;
    const wrappedEmit = (e: { type: string; payload?: any }) => {
      const t = e?.type;
      if ((t === 'llm/messages/init' || t === 'llm/messages/snapshot' || t === 'llm/messages/final') && e?.payload?.messages) {
        try {
          const featureMsgs: CompletionMessage[] = e.payload.messages || [];
          // Ensure every message is tagged with this feature id and agentType for persistence
          const ensured = featureMsgs.map((m, idx) => annotateMessage(m, { featureId: (m as any).featureId ?? next.id, agentType, turn: (m as any).turn ?? idx }));
          lastFeatureMessages = ensured.slice();
          // Update groups
          const g = ensureGroup(aggregatedGroups, next.id);
          g.messages = ensured.slice();
          if (t === 'llm/messages/final') g.endDate = new Date().toISOString();
          // Update flat aggregate for readability (kept for backward compatibility)
          const combined = aggregatedMessages.concat(ensured);
          const payload = { ...e.payload, featureId: next.id, messages: combined, messageGroups: { ...aggregatedGroups } };
          baseEmit?.({ ...e, payload });
          return;
        } catch {}
      }
      baseEmit?.(e);
    };

    await runAgentOnFeature(model, agentType, freshTask, next, tools, git, completion, wrappedEmit);

    // After the feature completes, fold its messages into the aggregate
    if (lastFeatureMessages && lastFeatureMessages.length) {
      aggregatedMessages = aggregatedMessages.concat(lastFeatureMessages);
      // Emit a snapshot to persist the full sequence after folding
      emit?.({ type: 'llm/messages/snapshot', payload: { messages: aggregatedMessages.slice(), messageGroups: { ...aggregatedGroups }, note: 'feature_folded', featureId: next.id } });
    }

    processed.add(next.id);
  }

  // Emit final snapshot at end of task run with complete sequence of feature conversations
  emit?.({ type: 'llm/messages/final', payload: { messages: aggregatedMessages.slice(), messageGroups: { ...aggregatedGroups }, note: 'task_run_complete' } });
}

// --- Isolated Runner (mimic Python run.py) ---

function shouldIgnoreCopy(relPath: string): boolean {
  // Ignore common build, cache, and IDE artifacts for TypeScript/Node/Electron projects
  // Preserve .git (do NOT ignore)
  const parts = relPath.split(path.sep);
  const name = parts[parts.length - 1] || relPath;

  // If path is within .git, do not ignore (preserve repo history/branches)
  if (parts.includes('.git')) return false;

  const dirIgnore = new Set([
    // Package managers and caches
    'node_modules', '.pnpm', '.pnp', '.pnp.cjs', '.pnp.js', '.npm', '.yarn', '.yarn-cache', '.yarnrc',
    // Build/output directories
    'dist', 'build', 'out', 'release', 'storybook-static',
    // Framework-specific
    '.next', 'next', '.nuxt', '.svelte-kit',
    // Bundlers and dev servers caches
    '.vite', '.rollup.cache', '.parcel-cache', '.webpack',
    // Test/coverage
    'coverage', '.nyc_output',
    // Monorepo/turbo caches
    '.turbo',
    // Generic caches
    '.cache', '.eslintcache', '.rpt2_cache', '.tscache',
    // Editors/IDE
    '.idea', '.vscode',
    // Python (from original mirror)
    'venv', '__pycache__',
    // Logs and temp
    'logs', 'tmp', 'temp'
  ]);

  // If any path segment matches an ignored dir name
  if (parts.some(p => dirIgnore.has(p))) return true;

  // Ignore specific file patterns
  const lower = name.toLowerCase();
  if (lower === '.ds_Store'.toLowerCase() || lower === 'thumbs.db') return true;
  if (lower.endsWith('.log')) return true; // npm/yarn/pnpm debug logs
  if (lower.endsWith('.pyc')) return true; // Python bytecode files
  if (lower.endsWith('.tsbuildinfo')) return true; // TypeScript incremental build info

  // Ignore Electron asar content markers when encountered as path segments
  if (parts.some(p => p.endsWith('.asar') || p.endsWith('.asar.unpacked'))) return true;

  return false;
}

async function copyTree(src: string, dest: string) {
  // Recursively copy, respecting ignore patterns and skipping asar/virtual entries
  let stat: fs.Stats;
  try {
    stat = await fsp.stat(src);
  } catch (e: any) {
    throw new Error(`Source path does not exist or is not accessible: ${src} (${e?.code || e})`);
    }
  if (!stat.isDirectory()) throw new Error(`Source ${src} is not a directory`);
  await fsp.mkdir(dest, { recursive: true });

  const walk = async (from: string, to: string) => {
    let entries: fs.Dirent[] = [] as unknown as fs.Dirent[];
    try {
      entries = await fsp.readdir(from, { withFileTypes: true });
    } catch (e: any) {
      // Skip unreadable directories (e.g., asar virtuals)
      logger.warn(`copyTree: Skipping unreadable directory ${from}: ${e?.code || e}`);
      return;
    }
    for (const entry of entries) {
      const rel = path.relative(src, path.join(from, entry.name));
      if (shouldIgnoreCopy(rel)) continue;
      const srcPath = path.join(from, entry.name);
      const dstPath = path.join(to, entry.name);

      // Skip any path that traverses into an asar archive
      if (srcPath.includes('.asar')) continue;

      try {
        if (entry.isDirectory()) {
          await fsp.mkdir(dstPath, { recursive: true });
          await walk(srcPath, dstPath);
        } else if (entry.isSymbolicLink()) {
          // Resolve symlink target and copy contents (best-effort)
          try {
            const linkTarget = await fsp.readlink(srcPath);
            const resolved = path.resolve(path.dirname(srcPath), linkTarget);
            const st = await fsp.stat(resolved);
            if (st.isDirectory()) {
              await fsp.mkdir(dstPath, { recursive: true });
              await walk(resolved, dstPath);
            } else {
              await fsp.copyFile(resolved, dstPath);
            }
          } catch {
            // Fallback: copy file bytes of the link itself (as file)
            try { await fsp.copyFile(srcPath, dstPath); } catch {}
          }
        } else if (entry.isFile()) {
          await fsp.copyFile(srcPath, dstPath);
        }
      } catch (e: any) {
        // Skip problematic entries (e.g., ENOENT due to virtualized packaging paths)
        if (e && (e.code === 'ENOENT' || e.code === 'EBUSY' || e.code === 'EPERM')) {
          logger.warn(`copyTree: Skipping ${srcPath}: ${e.code}`);
          continue;
        }
        throw e;
      }
    }
  };

  await walk(src, dest);
}

export async function runIsolatedOrchestrator(opts: {
  model: string;
  agentType: 'developer' | 'tester' | 'planner' | 'contexter' | 'speccer';
  projectId: string; 
  taskId: string;
  featureId?: string; 
  webSearchApiKeys?: WebSearchApiKeys;
  gitFactory: (projectRoot: string) => GitManager;  // git bound to projectRoot
  completion: CompletionClient;
  emit?: (e: { type: string; payload?: any }) => void;
}) {
  const { model, agentType, projectId, taskId, featureId, webSearchApiKeys, gitFactory, completion, emit } = opts;


  // Create temp workspace
  const tmpBase = fs.mkdtempSync(path.join(os.tmpdir(), 'thefactory-tools-'));
  // Keep a subfolder to host the copied source
  const workspace = path.join(tmpBase, 'workspace');

  const projectTools = createTools(process.cwd(), webSearchApiKeys);
  const projectDir = await projectTools.getProjectDir(projectId);

  logger.info(`Copying repository from '${projectDir}' to temporary workspace: '${workspace}'`);
  try {
    await copyTree(projectDir, workspace);
  } catch (e) {
    logger.error(`FATAL: Failed to copy repository to temporary directory from '${projectDir}' -> '${workspace}': ${e}`);
    // best-effort cleanup
    try { fs.rmSync(tmpBase, { recursive: true, force: true }); } catch {}
    return;
  }

  const git = gitFactory(workspace);
  const tools = createTools(workspace, webSearchApiKeys);

  try {
    await runOrchestrator({
      model,
      agentType,
      taskId,
      featureId,
      tools,
      git,
      completion,
      emit,
    });
  } catch (e) {
    logger.error(`An error occurred while running the agent orchestrator: ${e}`);
  } finally {
    // Cleanup temporary directory
    try {
      fs.rmSync(tmpBase, { recursive: true, force: true });
      logger.info('Agent run finished. Temporary directory cleaned up.');
    } catch (e) {
      logger.warn(`Failed to cleanup temporary directory '${tmpBase}': ${e}`);
    }
  }
}
