import { createTools } from './tools.js';
import { Feature, Task, WebSearchApiKeys } from '../types.js';
import { normalizeToolArguments } from '../utils.js';

export type ChatTool = {
  type: 'function';
  function: {
    name: string;
    description: string;
    parameters: any;
  };
};

const chatToolArgMappings: Record<string, Record<string, string[]>> = {
  get_task_reference: { reference: ['ref', 'id'] },
  list_files: { path: ['in', 'dir', 'directory'] },
  read_files: { paths: ['files', 'in', 'path'] },
  search_files: { query: ['q', 'search'], path: ['in', 'dir', 'directory'] },
  write_file: { name: ['filename', 'path', 'file'], content: ['text', 'data'] },
  web_search: { query: ['q', 'search'], providers: ['provider'] }
};

export function buildChatTools(opts: { repoRoot: string; projectId: string, webSearchApiKeys?: WebSearchApiKeys }) {
  const repoRoot = path.resolve(opts.repoRoot || process.cwd());

  const toolFunctions = createTools(repoRoot, opts.webSearchApiKeys)

  const tools: ChatTool[] = [
    {
      type: 'function',
      function: {
        name: 'list_tasks',
        description: 'List all tasks in the current project',
        parameters: { type: 'object', properties: {} },
      },
    },
    {
      type: 'function',
      function: {
        name: 'get_task_reference',
        description: 'Get a task or feature by its reference in the current project',
        parameters: {
          type: 'object',
          properties: {
            reference: { type: 'string', description: 'Task or feature reference (e.g., 1 or 1.2 - usually displayed to the user as #1 or #1.2)' },
          },
          required: ['reference'],
        },
      },
    },
    {
      type: 'function',
      function: {
        name: 'list_files',
        description: 'List all files in the current project',
        parameters: {
          type: 'object',
          properties: {
            path: { type: 'string', description: 'Project-relative path to the directory to list files and directories.' },
          },
          required: ['path'],
        },
      },
    },
    {
      type: 'function',
      function: {
        name: 'search_files',
        description: 'Search for files by name or textual content under the given path. Returns matching relative paths. The query input must be a space separated list of keywords',
        parameters: {
          type: 'object',
          properties: {
            query: { type: 'string', description: 'Space separated list of keywords to look for.' },
            path: { type: 'string', description: 'Project-relative path to the directory to start searching at. Defaults to root of the project if not given.' },
          },
          required: ['query'],
        },
      },
    },
    {
      type: 'function',
      function: {
        name: 'read_files',
        description: 'Read the contents of a list of files by their project-relative paths',
        parameters: {
          type: 'object',
          properties: {
            paths: { type: 'array', items: { type: 'string' }, description: 'Project-relative paths of files to read.' },
          },
          required: ['paths'],
        }
      }
    },
    {
      type: 'function',
      function: {
        name: 'write_file',
        description: 'Writes a new file with the given name and content (relative to project root)',
        parameters: {
          type: 'object',
          properties: {
            name: { type: 'string', description: 'Project-relative path (include extension, e.g. notes/todo.md)' },
            content: { type: 'string', description: 'Content of the file' },
          },
          required: ['name', 'content'],
        },
      },
    },
    {
      type: 'function',
      function: {
        name: 'web_search',
        description: 'Perform a web search with chosen providers, in order given and if configured. Available providers: exa, tavily, serpapi. If none chosen, defaults based on availability priority.',
        parameters: {
          type: 'object',
          properties: {
            query: { type: 'string', description: 'Query string' },
            limit: { type: 'number', description: 'Maximum number of results', default: 5 },
            providers: { type: 'arrays', items: { type: "string", enum: ['exa', 'tavily', 'serpapi'] }, description: "Order in which to pick providers. Defaults to ['exa','tavily','serpapi'] if none given." }
          },
          required: ['query']
        }
      }
    },
  ];

  async function getTaskReference(reference: string) : Promise<Task | Feature | undefined>
  {
    const ref = String(reference || '').trim().replace(/^#/, '');
    if (!ref) throw new Error('Invalid reference');
    const parts = ref.split('.');
    const taskIndex = parseInt(parts[0]) ?? -1;

    const taskId = await toolFunctions.getTaskIdFromIndex(opts.projectId, taskIndex)
    if (!taskId) throw new Error('task not found');

    if (parts.length <= 1) { return await toolFunctions.getTask(taskId) }

    const featureIndex = parseInt(parts[1]) ?? -1
    return await toolFunctions.getFeatureFromIndex(taskId, featureIndex)
  }

  async function callTool(name: string, args: any) {
    const mapping = chatToolArgMappings[name];
    const normalizedArgs = mapping ? normalizeToolArguments(args, mapping) : args;

    switch (name) {
      case 'list_tasks': return await toolFunctions.listTasks();
      case 'get_task_reference': return await getTaskReference(normalizedArgs?.reference ?? "");
      case 'list_files': return await toolFunctions.listFiles(normalizedArgs?.path ?? ".");
      case 'read_files': return await toolFunctions.readFiles(normalizedArgs?.paths ?? []);
      case 'search_files': return await toolFunctions.searchFiles(normalizedArgs?.query ?? "", normalizedArgs?.path ?? ".");
      case 'write_file': return await toolFunctions.writeFile(normalizedArgs?.name ?? "", normalizedArgs?.content ?? "");
      case 'web_search': return await toolFunctions.webSearch({query: normalizedArgs?.query ?? "", limit: normalizedArgs?.limit, providers: normalizedArgs?.providers});
      default: throw new Error(`Unknown tool: ${name}`);
    }
  }

  return { tools, callTool };
}

