  )
}
","src/renderer/hooks/useFilesAutocomplete.ts":"import { useEffect, useState } from 'react';

export function useFilesAutocomplete(params: {
  filesList: string[];
  input: string;
  setInput: (v: string) => void;
  textareaRef: React.RefObject<HTMLTextAreaElement>;
  mirrorRef: React.RefObject<HTMLDivElement>;
}) {
  const { filesList, input, setInput, textareaRef, mirrorRef } = params;
  const [isOpen, setIsOpen] = useState(false);
  const [matches, setMatches] = useState<string[]>([]);
  const [mentionStart, setMentionStart] = useState<number | null>(null);
  const [position, setPosition] = useState<{ left: number; top: number } | null>(null);
  const [caretPos, setCaretPos] = useState<number>(0);

  function getCursorCoordinates(textarea: HTMLTextAreaElement, pos: number) {
    const mirror = mirrorRef.current;
    if (!mirror) return { x: 0, y: 0 };

    const style = window.getComputedStyle(textarea);
    const stylesToCopy = [
      'boxSizing','borderBottomWidth','borderLeftWidth','borderRightWidth','borderTopWidth','fontFamily','fontSize','fontStyle','fontWeight','letterSpacing','lineHeight','paddingBottom','paddingLeft','paddingRight','paddingTop','textDecoration','textTransform','width'
    ] as const;
    stylesToCopy.forEach((key) => { (mirror.style as any)[key] = (style as any)[key]; });
    mirror.style.overflowWrap = 'break-word';
    mirror.style.whiteSpace = 'pre-wrap';
    mirror.style.wordBreak = 'break-word';
    mirror.style.height = 'auto';

    mirror.textContent = input.slice(0, pos);

    const marker = document.createElement('span');
    marker.style.display = 'inline-block';
    marker.style.width = '0';
    marker.textContent = '';
    mirror.appendChild(marker);

    const mirrorRect = mirror.getBoundingClientRect();
    const markerRect = marker.getBoundingClientRect();

    const x = markerRect.left - mirrorRect.left;
    const y = markerRect.top - mirrorRect.top;

    mirror.textContent = '';

    return { x, y };
  }

  const checkForMention = (text: string, pos: number) => {
    let start = pos;
    while (start > 0 && text[start - 1] !== ' ' && text[start - 1] !== '\n') {
      start--;
    }
    const word = text.slice(start, pos);
    if (word.startsWith('@')) {
      const query = word.slice(1);
      const filtered = filesList.filter((p) => p.toLowerCase().includes(query.toLowerCase()));
      setMatches(filtered);
      setMentionStart(start);
      if (filtered.length > 0) {
        const textarea = textareaRef.current!;
        const coords = getCursorCoordinates(textarea, pos);
        const textareaRect = textarea.getBoundingClientRect();
        const cursorLeft = textareaRect.left + coords.x;
        const topAboveTextarea = textareaRect.top + window.scrollY - 8; // 8px gap above input
        setPosition({ left: cursorLeft, top: topAboveTextarea });
        setIsOpen(true);
        return;
      }
    }
    setIsOpen(false);
  };

  const onSelect = (path: string) => {
    const textarea = textareaRef.current;
    if (!textarea || mentionStart === null) return;
    const currentText = textarea.value;
    const currentPos = textarea.selectionStart;
    const before = currentText.slice(0, mentionStart);
    const after = currentText.slice(currentPos);
    const newText = `${before}@${path} ${after}`;
    setInput(newText);
    const newPos = before.length + 1 + path.length;
    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(newPos, newPos);
    }, 0);
    setIsOpen(false);
    setMentionStart(null);
  };

  useEffect(() => {
    const handleSelectionChange = () => {
      const ta = textareaRef.current;
      if (!ta) return;
      if (document.activeElement === ta) {
        try {
          setCaretPos(ta.selectionStart ?? 0);
        } catch {
          // ignore
        }
      }
    };
    document.addEventListener('selectionchange', handleSelectionChange);
    return () => document.removeEventListener('selectionchange', handleSelectionChange);
  }, [textareaRef]);

  // Compute suggestions purely from the controlled input and current caret position
  // Avoid including 'matches' in deps to prevent update loops when matches state updates
  useEffect(() => {
    const textarea = textareaRef.current;
    if (!textarea) return;
    const pos = textarea.selectionStart ?? caretPos;
    checkForMention(input, pos);
  }, [input, caretPos]);

  return { isOpen, matches, position, onSelect };
}
","src/renderer/hooks/useReferencesAutocomplete.ts":"import { useEffect, useMemo, useState } from 'react';
import { useTasks } from './useTasks';
import { useActiveProject } from '../projects/ProjectContext';

type RefItem = {
  ref: string;
  display: string;
  title: string;
  type: 'task' | 'feature';
};

export function useReferencesAutocomplete(params: {
  input: string;
  setInput: (v: string) => void;
  textareaRef: React.RefObject<HTMLTextAreaElement>;
  mirrorRef: React.RefObject<HTMLDivElement>;
}) {
  const { project } = useActiveProject();
  const { input, setInput, textareaRef, mirrorRef } = params;
  const { tasksById } = useTasks();

  const references = useMemo<RefItem[]>(() => {
    if (!project) { return [] }
    const refs: RefItem[] = [];
    Object.values(tasksById).forEach((task) => {
      const taskDisplay = `${project.taskIdToDisplayIndex[task.id]}`
      refs.push({ ref: `${task.id}`, display: taskDisplay, title: task.title, type: 'task' });
      (task.features || []).forEach((f) => {
        const featureDisplay = `${task.featureIdToDisplayIndex[f.id]}`
        refs.push({ ref: `${task.id}.${f.id}`, display: `${taskDisplay}.${featureDisplay}`, title: f.title, type: 'feature' });
      });
    });
    return refs.sort((a, b) => a.ref.localeCompare(b.ref));
  }, [tasksById, project]);

  const [isOpen, setIsOpen] = useState(false);
  const [matches, setMatches] = useState<RefItem[]>([]);
  const [mentionStart, setMentionStart] = useState<number | null>(null);
  const [position, setPosition] = useState<{ left: number; top: number } | null>(null);
  const [caretPos, setCaretPos] = useState<number>(0);

  function getCursorCoordinates(textarea: HTMLTextAreaElement, pos: number) {
    const mirror = mirrorRef.current;
    if (!mirror) return { x: 0, y: 0 };

    const style = window.getComputedStyle(textarea);
    const stylesToCopy = [
      'boxSizing',
      'borderBottomWidth',
      'borderLeftWidth',
      'borderRightWidth',
      'borderTopWidth',
      'fontFamily',
      'fontSize',
      'fontStyle',
      'fontWeight',
      'letterSpacing',
      'lineHeight',
      'paddingBottom',
      'paddingLeft',
      'paddingRight',
      'paddingTop',
      'textDecoration',
      'textTransform',
      'width',
    ] as const;
    stylesToCopy.forEach((key) => {
      (mirror.style as any)[key] = (style as any)[key];
    });
    mirror.style.overflowWrap = 'break-word';
    mirror.style.whiteSpace = 'pre-wrap';
    mirror.style.wordBreak = 'break-word';
    mirror.style.height = 'auto';

    mirror.textContent = input.slice(0, pos);

    const marker = document.createElement('span');
    marker.style.display = 'inline-block';
    marker.style.width = '0';
    marker.textContent = '';
    mirror.appendChild(marker);

    const mirrorRect = mirror.getBoundingClientRect();
    const markerRect = marker.getBoundingClientRect();

    const x = markerRect.left - mirrorRect.left;
    const y = markerRect.top - mirrorRect.top;

    mirror.textContent = '';

    return { x, y };
  }

  const isBoundaryChar = (ch: string) => {
    return /[\s\n\t\(\)\[\]\{\}<>,.!?:;"'`]/.test(ch);
  };

  const checkForMention = (text: string, pos: number) => {
    let start = pos;
    while (start > 0 && !isBoundaryChar(text[start - 1])) {
      start--;
    }
    const word = text.slice(start, pos);

    if (word.startsWith('#')) {
      const query = word.slice(1).toLowerCase();
      const filtered = references.filter(
        (item) => item.display.toLowerCase().startsWith(query) || item.title.toLowerCase().includes(query)
      );
      setMatches(filtered);
      setMentionStart(start);

      const textarea = textareaRef.current!;
      const coords = getCursorCoordinates(textarea, pos);
      const textareaRect = textarea.getBoundingClientRect();
      const cursorLeft = textareaRect.left + window.scrollX + coords.x;
      const topAboveTextarea = textareaRect.top + window.scrollY - 8;
      setPosition({ left: cursorLeft, top: topAboveTextarea });
      setIsOpen(true);
      return;
    }

    setIsOpen(false);
    setMentionStart(null);
  };

  const onSelect = (selectedRef: string) => {
    const textarea = textareaRef.current;
    if (!textarea || mentionStart === null) return;
    const currentText = textarea.value;
    const currentPos = textarea.selectionStart;
    const before = currentText.slice(0, mentionStart);
    const after = currentText.slice(currentPos);
    const newText = `${before}#${selectedRef}${after}`;
    setInput(newText);
    const newPos = before.length + 1 + selectedRef.length;
    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(newPos, newPos);
    }, 0);
    setIsOpen(false);
    setMentionStart(null);
  };

  useEffect(() => {
    const handleSelectionChange = () => {
      const ta = textareaRef.current;
      if (!ta) return;
      if (document.activeElement === ta) {
        try {
          setCaretPos(ta.selectionStart ?? 0);
        } catch {
          // ignore
        }
      }
    };
    document.addEventListener('selectionchange', handleSelectionChange);
    return () => document.removeEventListener('selectionchange', handleSelectionChange);
  }, [textareaRef]);

  useEffect(() => {
    const textarea = textareaRef.current;
    if (!textarea) return;
    const pos = textarea.selectionStart ?? caretPos;
    checkForMention(input, pos);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [input, references, caretPos]);

  return { isOpen, matches, position, onSelect };
}
","src/renderer/components/tasks/FeatureForm.tsx":"import React, { useEffect, useMemo, useRef, useState } from 'react'
import type { Status } from 'packages/factory-ts/src/types'
import StatusControl from './StatusControl'
import { DependencySelector } from './DependencySelector'
import DependencyBullet from './DependencyBullet'
import { FileSelector } from '../ui/FileSelector'
import ContextFileChip from './ContextFileChip'
import { Modal } from '../ui/Modal'
import { IconDelete, IconPlus } from '../ui/Icons'
import FileMentionsTextarea from '../ui/FileMentionsTextarea'

export type FeatureFormValues = {
  title: string
  description?: string
  rejection?: string
  status: Status
  blockers?: string[]
  context: string[]
}

type Props = {
  initialValues?: Partial<FeatureFormValues>
  onSubmit: (values: FeatureFormValues) => void | Promise<void>
  onCancel: () => void
  onDelete?: () => void
  submitting?: boolean
  titleRef?: React.RefObject<HTMLInputElement>
  taskId: string
  featureId?: string
}

export default function FeatureForm({
  initialValues,
  onSubmit,
  onCancel,
  onDelete,
  submitting = false,
  titleRef,
  taskId,
  featureId = undefined,
}: Props) {
  const [title, setTitle] = useState<string>(initialValues?.title ?? '')
  const [description, setDescription] = useState<string>(initialValues?.description ?? '')
  const [rejection, setRejection] = useState<string>(initialValues?.rejection ?? '')
  const [status, setStatus] = useState<Status>(initialValues?.status ?? '-')
  const [blockers, setBlockers] = useState<string[]>(initialValues?.blockers ?? [])
  const [context, setContext] = useState<string[]>(initialValues?.context ?? [])
  const [error, setError] = useState<string | null>(null)
  const [showSelector, setShowSelector] = useState(false)
  const [showFileSelector, setShowFileSelector] = useState(false)

  const localTitleRef = useRef<HTMLInputElement>(null)
  const combinedTitleRef = titleRef ?? localTitleRef
  const isCreate = !(featureId !== null && featureId !== undefined)

  useEffect(() => {
    if (combinedTitleRef?.current) {
      combinedTitleRef.current.focus()
      combinedTitleRef.current.select?.()
    }
  }, [combinedTitleRef])

  const canSubmit = useMemo(() => title.trim().length > 0 && !submitting, [title, submitting])

  function validate(): boolean {
    let valid = true
    if (!title.trim()) {
      setError('Title is required')
      valid = false
    } else {
      setError(null)
    }

    return valid
  }

  async function handleSubmit(e?: React.FormEvent) {
    e?.preventDefault()
    if (!validate()) return
    const payload: FeatureFormValues = {
      title: title.trim(),
      description: description?.trim() || '',
      rejection: rejection?.trim() || undefined,
      status,
      blockers,
      context,
    }
    await onSubmit(payload)
  }

  function onKeyDown(e: React.KeyboardEvent) {
    if ((e.metaKey || e.ctrlKey) && e.key.toLowerCase() === 'enter') {
      e.preventDefault()
      if (canSubmit) handleSubmit()
    }
  }

  function removeBlockerAt(idx: number) {
    setBlockers((deps) => deps.filter((_, i) => i !== idx))
  }

  function removeContextAt(idx: number) {
    setContext((ctx) => ctx.filter((_, i) => i !== idx))
  }

  const combinedTextForMentions = `${title}\n${description}\n${rejection}`
  const mentionedPaths = useMemo(() => {
    const res = new Set<string>()
    for (const p of context) {
      if (combinedTextForMentions.includes(`@${p}`)) res.add(p)
    }
    return res
  }, [combinedTextForMentions, context])

  const handleFileMentionSelected = (path: string) => {
    setContext((prev) => (prev.includes(path) ? prev : [...prev, path]))
  }

  return (
    <form onSubmit={handleSubmit} onKeyDown={onKeyDown} className="space-y-4" aria-label={isCreate ? 'Create Feature' : 'Edit Feature'}>
      <div className="grid grid-cols-1 gap-3">
        <StatusControl
          status={status}
          onChange={setStatus}
        />
        <div className="flex items-center gap-3">
          <label htmlFor="feature-title" className="text-xs flex-1" style={{ color: 'var(--text-secondary)' }}>Title</label>
        </div>
        <input
          id="feature-title"
          ref={combinedTitleRef}
          type="text"
          placeholder="What is this feature?"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          disabled={submitting}
          className="w-full rounded-md border px-3 py-2 text-sm disabled:opacity-60"
          style={{
            background: 'var(--surface-raised)',
            borderColor: error ? 'var(--status-stuck-soft-border)' : 'var(--border-default)',
            color: 'var(--text-primary)'
          }}
          aria-invalid={!!error}
          aria-describedby={error ? 'feature-title-error' : undefined}
        />
        {error ? (
          <div id="feature-title-error" className="text-xs" style={{ color: 'var(--status-stuck-fg)' }}>{error}</div>
        ) : null}

        <div className="flex flex-col gap-1">
          <label htmlFor="feature-description" className="text-xs" style={{ color: 'var(--text-secondary)' }}>Description</label>
          <FileMentionsTextarea
            id="feature-description"
            rows={4}
            placeholder="Optional details or acceptance criteria"
            value={description}
            onChange={setDescription}
            disabled={submitting}
            className="w-full rounded-md border px-3 py-2 text-sm disabled:opacity-60 resize-y max-h-64"
            style={{ background: 'var(--surface-raised)', borderColor: 'var(--border-default)', color: 'var(--text-primary)' }}
            ariaLabel="Feature description"
            onFileMentionSelected={handleFileMentionSelected}
          />
        </div>

        <div className="flex flex-col gap-1">
          <label htmlFor="feature-rejection" className="text-xs" style={{ color: 'var(--text-secondary)' }}>Rejection Reason</label>
          <FileMentionsTextarea
            id="feature-rejection"
            rows={3}
            placeholder="Optional reason for rejection (leave blank to remove)"
            value={rejection}
            onChange={setRejection}
            disabled={submitting}
            className="w-full rounded-md border px-3 py-2 text-sm disabled:opacity-60 resize-y max-h-64"
            style={{ background: 'var(--surface-raised)', borderColor: 'var(--border-default)', color: 'var(--text-primary)' }}
            ariaLabel="Feature rejection reason"
            onFileMentionSelected={handleFileMentionSelected}
          />
        </div>

        <div className="flex flex-col gap-1">
          <label className="text-xs" style={{ color: 'var(--text-secondary)' }}>Context Files</label>
          <div className="flex flex-wrap items-start gap-2 border rounded-md min-h-[3rem] p-2" style={{ borderColor: 'var(--border-default)', background: 'var(--surface-raised)' }}>
            {context.map((p, idx) => (
              <ContextFileChip key={p} path={p} onRemove={() => removeContextAt(idx)} warn={!mentionedPaths.has(p)} />
            ))}
            <button type="button" onClick={() => setShowFileSelector(true)} className="chip chip--ok" title="Add context files">
              <IconPlus className="w-3 h-3" />
              <span>Add</span>
            </button>
          </div>
          <div className="text-xs text-text-muted">Select any files across the project that provide useful context for this feature. Tip: type @ in description to quickly reference files.</div>
        </div>

        <div className="flex flex-col gap-1">
          <label htmlFor="feature-blockers" className="text-xs" style={{ color: 'var(--text-secondary)' }}>Blockers</label>
          <div
            id="feature-blockers"
            className="chips-list border rounded-md min-h-[3rem] p-2"
            style={{
              borderColor: 'var(--border-default)',
              background: 'var(--surface-raised)'
            }}
          >
            {blockers.map((dep, idx) => {
              return (
                <DependencyBullet key={dep} dependency={dep} onRemove={() => removeBlockerAt(idx)} />
              )
            })}
            <button
              type="button"
              onClick={() => setShowSelector(true)}
              className="chip chip--ok"
              title="Add blocker"
            >
              <IconPlus className="w-3 h-3" />
              <span>Add</span>
            </button>
          </div>
        </div>
      </div>

      <div className="flex justify-between gap-2 pt-2">
        {onDelete && !isCreate ? (
          <button
            type="button"
            onClick={onDelete}
            disabled={submitting}
            className="btn"
            style={{
              background: 'var(--status-stuck-bg)',
              color: 'var(--status-stuck-fg)'
            }}
          >
            <IconDelete className="w-4 h-4" />
            Delete
          </button>
        ) : null}
        <div className="flex justify-end gap-2 flex-1">
          <button
            type="button"
            className="btn-secondary"
            onClick={() => onCancel()}
            disabled={submitting}
          >
            Cancel
          </button>
          <button
            type="submit"
            className="btn"
            disabled={!canSubmit}
            aria-keyshortcuts="Control+Enter Meta+Enter"
            title="Cmd/Ctrl+Enter to submit"
          >
            {isCreate ? 'Create Feature' : 'Save Changes'}
          </button>
        </div>
      </div>

      {showSelector && (
        <Modal title="Select Blocker" onClose={() => setShowSelector(false)} isOpen={true} size="md">
          <DependencySelector
            onConfirm={(deps) => {
              const newDeps = deps.filter((d) => !blockers.includes(d))
              setBlockers([...blockers, ...newDeps])
              setShowSelector(false)
            }}
            currentTaskId={taskId}
            currentFeatureId={featureId}
            existingDeps={blockers}
          />
        </Modal>
      )}

      {showFileSelector && (
        <Modal title="Select Context Files" onClose={() => setShowFileSelector(false)} isOpen={true} size="lg">
          <FileSelector
            selected={context}
            onCancel={() => setShowFileSelector(false)}
            onConfirm={(paths) => {
              const unique = Array.from(new Set([...(context || []), ...paths]))
              setContext(unique)
              setShowFileSelector(false)
            }}
            allowMultiple
          />
        </Modal>
      )}
    </form>
  )
}
"}

