  try {
    const dbPath = path.join(projectRoot, '.factory', 'history.sqlite');
    if (typeof createHistoryStore === 'function') {
      console.log('[factory] Initializing history store at', dbPath);
      HISTORY = createHistoryStore({ dbPath });
    } else {
