","src/projects/ProjectsManager.js":"import fs from 'fs/promises';
import path from 'path';
import chokidar from 'chokidar';
import { ipcMain } from 'electron';
import { validateProjectSpec } from './ProjectsValidator';
import IPC_HANDLER_KEYS from "../ipcHandlersKeys"

async function pathExists(p) { try { await fs.stat(p); return true } catch { return false } }

export class ProjectsManager {
  constructor(projectRoot, window) {
    this.projectRoot = path.isAbsolute(projectRoot) ? projectRoot : path.resolve(projectRoot);
    this.projectsDir = path.join(this.projectRoot, 'projects');
    this.window = window;
    this.watcher = null;
    this._ipcBound = false;

    this.projects = []
  }

  async init() {
    await this.__buildIndex();
    if (await pathExists(this.projectsDir)) {
      this.watcher = chokidar.watch(path.join(this.projectsDir, '/*.json'), {
        ignored: /(^|[\/\\])\../,
        persistent: true,
        ignoreInitial: true,
      });
      this.watcher
        .on('add', (p) => this.__rebuildAndNotify(`Project config added: ${p}`))
        .on('change', (p) => this.__rebuildAndNotify(`Project config changed: ${p}`))
        .on('unlink', (p) => this.__rebuildAndNotify(`Project config removed: ${p}`))
        .on('addDir', (p) => this.__rebuildAndNotify(`Dir added: ${p}`))
        .on('unlinkDir', (p) => this.__rebuildAndNotify(`Dir removed: ${p}`));
    }
    this._registerIpcHandlers();
  }

  stopWatching() {
    if (this.watcher) {
      this.watcher.close();
      this.watcher = null;
    }
  }

  __notify(msg) {
    if (msg) console.log(msg);
    if (this.window) {
      this.window.webContents.send(IPC_HANDLER_KEYS.PROJECTS_SUBSCRIBE, this.projects);
    }
  }
  async __rebuildAndNotify(msg) {
    await this.__buildIndex();
    this.__notify(msg)
  }

  async __buildIndex() {
    const projectsDirAbs = path.resolve(this.projectsDir);
    const rootAbs = path.resolve(this.projectRoot);

    const projects = []

    if (await pathExists(projectsDirAbs)) {
      let entries = [];
      try { entries = await fs.readdir(projectsDirAbs, { withFileTypes: true }); } catch (e) {
        if (e && e.code !== 'ENOENT') next.errors.push({ type: 'readdir', dir, message: e.message || String(e) });
        return;
      }
      for (const entry of entries) {
        const abs = path.join(projectsDirAbs, entry.name);
        if (entry.isFile() && entry.name.toLowerCase().endsWith('.json')) {
          const project = await this._tryLoadProjectConfig(abs, projectsDirAbs, rootAbs);
          if (project){
            projects.push(project)
          }
        }
      }
    }

    this.projects = projects
  }

  async _tryLoadProjectConfig(configAbsPath, projectsDirAbs, rootAbs) {
    let raw;
    try {
      raw = await fs.readFile(configAbsPath, 'utf8');
    } catch (e) {
      return;
    }

    let json;
    try {
      json = JSON.parse(raw);
    } catch (e) {
      return;
    }

    const { valid, errors } = validateProjectSpec(json);
    if (!valid) {
      return;
    }

    // Normalize and security-check path field to stay within projects directory
    // const declaredPath = json.path;
    // const resolved = path.isAbsolute(declaredPath)
    //   ? path.resolve(declaredPath)
    //   : path.resolve(projectsDirAbs, declaredPath);

    // const normalizedProjects = projectsDirAbs;
    // if (!(resolved + path.sep).startsWith(normalizedProjects + path.sep) && resolved !== normalizedProjects) {
    //   next.errors.push({ type: 'security', file: configAbsPath, message: `Project path escapes projects directory: ${resolved}` });
    //   return;
    // }

    return json
  }

  _registerIpcHandlers() {
    if (this._ipcBound) return;

    const handlers = {  }
    handlers[IPC_HANDLER_KEYS.PROJECTS_LIST] = () => this.listProjects()
    handlers[IPC_HANDLER_KEYS.PROJECTS_GET] = (args) => this.getProject(args.id)
    handlers[IPC_HANDLER_KEYS.PROJECTS_CREATE] = (args) => this.createProject(args.project)
    handlers[IPC_HANDLER_KEYS.PROJECTS_UPDATE] = (args) => this.updateProject(args.id, args.project)
    handlers[IPC_HANDLER_KEYS.PROJECTS_DELETE] = (args) => this.deleteProject(args.id)
    handlers[IPC_HANDLER_KEYS.PROJECTS_TASK_REORDER] = async (args) => this.reorderTask(args.projectId, args.fromIndex, args.toIndex)

    for(const handler of Object.keys(handlers)){
      ipcMain.handle(handler, async (event, args) => {
        try {
          return await handlers[handler](args)
        } catch (e) {
          console.error(`${handler} failed`, e);
          return { ok: false, error: String(e?.message || e) };
        }
      });
    }

    this._ipcBound = true;
  }

  async ensureProjectsDirExists() {
    const dir = this.projectsDir;
    try {
      await fs.mkdir(dir, { recursive: true });
    } catch {}
    return dir;
  }

  getProjectConfigPathForId(id) {
    return path.join(this.projectsDir, `${id}.json`);
  }

  async listProjects() {
    return this.projects
  }

  async getProject(id) {
    return this.projects.find(p => p.id === id)
  }

  async createProject(spec) {
    const sanitized = { ...spec };
    if (!Array.isArray(sanitized.requirements)) sanitized.requirements = [];
    const { valid, errors } = validateProjectSpec(sanitized);
    if (!valid) return { ok: false, error: 'Invalid project spec', details: errors };

    const dir = await this.ensureProjectsDirExists();

    const project = this.getProject(sanitized.id)
    if (project) {
      return project
    }

    const target = path.join(dir, `${sanitized.id}.json`);
    await fs.writeFile(target, JSON.stringify(sanitized, null, 2), 'utf8');

    this.projects.push(sanitized)
    await this.__notify('Project created')
    return sanitized
  }

  async updateProject(id, spec) {
    const sanitized = { ...spec };
    if (!Array.isArray(sanitized.requirements)) sanitized.requirements = [];
    if (!sanitized.id) sanitized.id = id;
    const { valid, errors } = validateProjectSpec(sanitized);
    if (!valid) return { ok: false, error: 'Invalid project spec', details: errors };

    await this.ensureProjectsDirExists();
    const existingPath = this.getProjectConfigPathForId(id);
    const writePath = this.getProjectConfigPathForId(sanitized.id);

    await fs.writeFile(writePath, JSON.stringify(sanitized, null, 2), 'utf8');
    if (await pathExists(existingPath) && path.resolve(existingPath) !== path.resolve(writePath)) {
      try { await fs.unlink(existingPath); } catch {}
    }

    this.projects = this.projects.map(p => p.id === sanitized.id ? sanitized : p)
    await this.__notify('Project updated');
    return sanitized
  }

  async deleteProject(id) {
    const p = this.getProjectConfigPathForId(id);
    if (await pathExists(p)) {
      await fs.unlink(p);
      this.projects = this.projects.filter(p => p.id !== id)
      await this.__notify('Project deleted');
    }
  }

  async reorderTask(projectId, fromIndex, toIndex)
  {
    const project = await this.getProject(projectId)
    if (!project){ throw new Error(`Project with id: ${projectId} not found`)}

    const currentOrder = Object.keys(project.taskIdToDisplayIndex).sort((a, b) => project.taskIdToDisplayIndex[a] - project.taskIdToDisplayIndex[b]);

    let newOrder;
    if (fromIndex !== undefined && toIndex !== undefined) {
      if (fromIndex < 0 || fromIndex >= currentOrder.length) throw new Error('Invalid source index');
      if (toIndex < 0 || toIndex > currentOrder.length) throw new Error('Invalid target index');
      newOrder = [...currentOrder];
      const [moved] = newOrder.splice(fromIndex, 1);
      newOrder.splice(toIndex, 0, moved);
    } else {
      throw new Error('Invalid indices for reorder');
    }

    if (JSON.stringify(newOrder) === JSON.stringify(currentOrder)) {
      return { ok: true };
    }

    const newIndex = {};
    newOrder.forEach((id, i) => {
      newIndex[id] = i + 1;
    });
    project.taskIdToDisplayIndex = newIndex;

    const writePath = this.getProjectConfigPathForId(projectId);
    await fs.writeFile(writePath, JSON.stringify(project, null, 2), 'utf8');

    this.projects = this.projects.map(p => p.id === project.id ? project : p)

    await this.__notify('Task reordered');
    return { ok: true };
  }
}
","src/renderer/projects/ProjectManagerModal.tsx":"import React, { useEffect, useMemo, useRef, useState } from 'react'
import { Modal } from '../components/ui/Modal'
import { projectsService } from '../services/projectsService'
import { validateProjectClient } from './validateProject'
import { useProjectContext } from './ProjectContext'
import { IconDelete, IconEdit, IconPlay, IconPlus } from '../components/ui/Icons'
import { Button } from '../components/ui/Button'
import { PROJECT_ICONS } from './projectIcons'

function TextInput({ label, value, onChange, placeholder, disabled }: any) {
  const id = React.useId()
  return (
    <div className="form-row">
      <label htmlFor={id}>{label}</label>
      <input id={id} className="ui-input" value={value} onChange={(e) => onChange(e.target.value)} placeholder={placeholder} disabled={disabled} />
    </div>
  )
}

function TextArea({ label, value, onChange, placeholder }: any) {
  const id = React.useId()
  return (
    <div className="form-row">
      <label htmlFor={id}>{label}</label>
      <textarea id={id} className="ui-textarea" value={value} onChange={(e) => onChange(e.target.value)} placeholder={placeholder} />
    </div>
  )
}

function IconPicker({ value, onChange }: { value?: string; onChange: (v: string) => void }) {
  return (
    <div className="form-row">
      <label>Icon</label>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(40px, 1fr))', gap: 6 }}>
        {PROJECT_ICONS.map((opt) => {
          const selected = value === opt.value
          return (
            <button
              key={opt.value}
              type="button"
              title={opt.label}
              onClick={() => onChange(opt.value)}
              aria-pressed={selected}
              style={{
                height: 36,
                borderRadius: 6,
                display: 'inline-flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: selected ? '2px solid var(--accent-primary)' : '1px solid var(--border-default)',
                background: selected ? 'color-mix(in srgb, var(--accent-primary) 10%, transparent)' : 'var(--surface-raised)',
                cursor: 'pointer',
                fontSize: 18,
              }}
            >
              <span aria-hidden>{opt.value}</span>
            </button>
          )
        })}
      </div>
    </div>
  )
}

export default function ProjectManagerModal({ onRequestClose, initialMode, initialProjectId }: { onRequestClose?: () => void, initialMode?: 'list' | 'create' | 'edit', initialProjectId?: string }) {
  const { projects, getProjectById } = useProjectContext()
  const [error, setError] = useState<string | null>(null)

  const [mode, setMode] = useState<'list' | 'create' | 'edit'>(initialMode || 'list')
  const [editingId, setEditingId] = useState<string | null>(initialProjectId || null)

  const [form, setForm] = useState<any>({ id: '', title: '', description: '', path: '', repo_url: '', requirements: [], metadata: { icon: 'üìÅ' } })
  const [formErrors, setFormErrors] = useState<string[]>([])
  const [saving, setSaving] = useState(false)
  const titleRef = useRef<HTMLInputElement>(null)

  const doClose = () => {
    onRequestClose?.()
  }

  useEffect(() => {
    // If we were asked to open in edit mode for a specific project, populate form
    if ((initialMode === 'edit' || mode === 'edit') && (initialProjectId || editingId)) {
      const id = (initialProjectId || editingId) as string
      const p: any = getProjectById(id)
      if (p) {
        setForm({ ...p, requirements: Array.isArray(p.requirements) ? p.requirements : [], metadata: {...(p.metadata ?? {}), icon: p.metadata?.icon || 'üìÅ' }})
        setEditingId(id)
        setMode('edit')
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [initialMode, initialProjectId, getProjectById])

  const projectsList = useMemo(() => projects || [], [projects])

  function resetForm() {
    setForm({ id: '', title: '', description: '', path: '', repo_url: '', requirements: [], metadata: { icon: 'üìÅ' } })
    setFormErrors([])
    setSaving(false)
    setEditingId(null)
  }

  function startCreate() {
    resetForm()
    setMode('create')
  }

  function startEdit(p: any) {
    setForm({ ...p, requirements: Array.isArray(p.requirements) ? p.requirements : [], metadata: {...(p.metadata ?? {}), icon: p.metadata?.icon || 'üìÅ' }})
    setEditingId(p.id)
    setMode('edit')
  }

  async function handleDelete(id: string) {
    if (!confirm('Delete this project configuration?')) return
    setSaving(true)
    const res = await projectsService.deleteProjct(id)
    if (!res.ok) {
      alert('Failed to delete: ' + (res.error || 'Unknown error'))
    }
    setSaving(false)
    setMode('list')
  }

  async function handleSubmit(e: React.FormEvent) {
    e.preventDefault()
    setFormErrors([])

    // client-side validation
    const v = validateProjectClient(form)
    if (!v.valid) { setFormErrors(v.errors); return }

    // Additional: unique id on create
    if (mode === 'create' && projectsList.find((p: any) => p.id === form.id)) {
      setFormErrors([`Project id ${form.id} already exists`])
      return
    }

    setSaving(true)
    try {
      if (mode === 'create') {
        await projectsService.createProject(form)
      } else if (mode === 'edit' && editingId) {
        await projectsService.updateProject(editingId, form)
      }
    } catch (e: any) {
      setFormErrors([e?.message || String(e)])
      return
    } finally {
      setSaving(false)
    }
    setMode('list')
  }

  return (
    <Modal title="Manage Projects" onClose={doClose} isOpen={true} size="lg" initialFocusRef={titleRef as React.RefObject<HTMLElement>}>
      {error && <div role="alert" style={{ color: 'var(--status-stuck-fg)' }}>Error: {error}</div>}

      {mode === 'list' && (
        <div className="flex flex-col" style={{ gap: 12 }}>
          <div className="flex" style={{ justifyContent: 'space-between', alignItems: 'center' }}>
            <div style={{ color: 'var(--text-secondary)' }}>Projects: {projectsList.length}</div>
            <button className="btn" onClick={startCreate}><IconPlus/></button>
          </div>
          <div>
            {projectsList.length === 0 && <div className="empty">No child projects yet.</div>}
            <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
              {projectsList.map((p: any) => (
                <li key={p.id} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', borderBottom: '1px solid var(--border-subtle)', padding: '8px 0' }}>
                  <div className="flex" style={{ alignItems: 'center', gap: 8 }}>
                    <div style={{ fontSize: 18 }} aria-hidden>{p.metadata?.icon || 'üìÅ'}</div>
                    <div>
                      <div style={{ fontWeight: 600 }}>{p.title}</div>
                      <div style={{ fontSize: 12, color: 'var(--text-secondary)' }}>{p.id} ¬∑ {p.path}</div>
                    </div>
                  </div>
                  <div className="flex" style={{ gap: 8 }}>
                    <Button className="btn-secondary" onClick={() => startEdit(p)}><IconEdit/></Button>
                    <Button className="btn-secondary" disabled={saving} variant="danger" onClick={() => handleDelete(p.id)}><IconDelete/></Button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}

      {(mode === 'create' || mode === 'edit') && (
        <form className="task-form" onSubmit={handleSubmit}>
          {formErrors.length > 0 && (
            <div role="alert" style={{ color: 'var(--status-stuck-fg)' }}>
              {formErrors.map((e, i) => <div key={i}>‚Ä¢ {e}</div>)}
            </div>
          )}
          <TextInput label="ID" value={form.id} onChange={(v: string) => setForm((s: any) => ({ ...s, id: v }))} placeholder="unique-id" disabled={mode === 'edit'} />
          <TextInput label="Title" value={form.title} onChange={(v: string) => setForm((s: any) => ({ ...s, title: v }))} placeholder="Project title" />
          <TextArea label="Description" value={form.description} onChange={(v: string) => setForm((s: any) => ({ ...s, description: v }))} placeholder="Short description" />
          <TextInput label="Path (under projects/)" value={form.path} onChange={(v: string) => setForm((s: any) => ({ ...s, path: v }))} placeholder="my-project" />
          <TextInput label="Repository URL" value={form.repo_url} onChange={(v: string) => setForm((s: any) => ({ ...s, repo_url: v }))} placeholder="https://github.com/org/repo" />
          <IconPicker value={form.metadata.icon} onChange={(v) => setForm((s: any) => ({ ...s, metadata: { ...s.metadata, icon: v }}))} />
          <div className="form-actions">
            <button type="button" className="btn-secondary" onClick={() => setMode('list')}>Cancel</button>
            <button type="submit" className="btn" disabled={saving}>{mode === 'create' ? 'Create' : 'Save'}</button>
          </div>
        </form>
      )}
    </Modal>
  )
}
"}

