      });
    }

    this._ipcBound = true;
  }

  async ensureProjectsDirExists() {
    const dir = this.projectsDir;
    try {
      await fs.mkdir(dir, { recursive: true });
    } catch {}
    return dir;
  }

  getProjectConfigPathForId(id) {
    return path.join(this.projectsDir, `${id}.json`);
  }

  async listProjects() {
