Tool read_files returned: {"src/renderer/components/agents/AgentRunRow.tsx":"import React, { useMemo } from 'react';
import type { AgentRun } from '../../services/agentsService';
import DependencyBullet from '../tasks/DependencyBullet';
import StatusChip from './StatusChip';
import TurnChip from './TurnChip';
import ModelChip from './ModelChip';
import { IconChevron, IconDelete } from '../ui/Icons';

function formatUSD(n?: number) {
  if (n == null) return '\u2014';
  return `$${n.toFixed(4)}`;
}

function formatTime(iso?: string) {
  if (!iso) return '';
  try {
    const d = new Date(iso);
    return d.toLocaleTimeString();
  } catch {
    return iso ?? '';
  }
}

function formatDate(iso?: string) {
  if (!iso) return '';
  try {
    const d = new Date(iso);
    return d.toLocaleDateString();
  } catch {
    return iso ?? '';
  }
}

function countTurns(messages: AgentRun['messages']): number {
  if (!messages || messages.length === 0) return 0;
  let turns = 0;
  let hasAssistant = false;
  for (const m of messages) {
    if ((m.role || '').toLowerCase() === 'user') turns++;
    if ((m.role || '').toLowerCase() === 'assistant') hasAssistant = true;
  }
  if (turns === 0 && hasAssistant) return 1;
  return turns;
}

function formatDuration(ms?: number) {
  if (ms == null || !isFinite(ms) || ms < 0) return '\u2014';
  if (ms < 1000) return `${Math.round(ms)}ms`;
  const s = Math.floor(ms / 1000);
  const hrs = Math.floor(s / 3600);
  const mins = Math.floor((s % 3600) / 60);
  const secs = s % 60;
  const parts: string[] = [];
  if (hrs) parts.push(`${hrs}h`);
  if (mins) parts.push(`${mins}m`);
  parts.push(`${secs}s`);
  return parts.join(' ');
}

export interface AgentRunRowProps {
  run: AgentRun;
  onView?: (runId: string) => void;
  onCancel?: (runId: string) => void;
  showActions?: boolean;
  showProject?: boolean;
  showModel?: boolean;
}

export default function AgentRunRow({ run, onView, onCancel, showActions = true, showProject = false, showModel = true }: AgentRunRowProps) {
  const turns = countTurns(run.messages);
  const started = useMemo(() => new Date(run.startedAt || run.updatedAt || Date.now()), [run.startedAt, run.updatedAt]);
  const ended = useMemo(() => (run.state === 'running' ? new Date() : new Date(run.updatedAt || Date.now())), [run.state, run.updatedAt]);
  const durationMs = Math.max(0, ended.getTime() - started.getTime());
  const dep = run.featureId ? `${run.taskId}.${run.featureId}` : run.taskId;

  return (
    <tr id={`run-${run.runId ?? 'unknown'}`} className="border-t border-neutral-200 dark:border-neutral-800 group">
      <td className="px-3 py-2 leading-tight">
        <div>{formatDate(run.startedAt)}</div>
        <div className="text-neutral-500">{formatTime(run.startedAt)}</div>
      </td>
      {showProject ? (
        <td className="px-3 py-2 text-xs font-mono text-neutral-600 dark:text-neutral-400">{run.projectId ?? '\u2014'}</td>
      ) : null}
      <td className="px-3 py-2">
        {dep ? (
          <DependencyBullet className={"max-w-[100px] overflow-clip"} dependency={dep} notFoundDependencyDisplay={"?"} />
        ) : (
          <span className="text-xs text-neutral-500">\u2014</span>
        )}
      </td>
      <td className="px-3 py-2">
        <StatusChip state={run.state} />
      </td>
      {showModel ? (
        <td className="px-3 py-2">
          <ModelChip provider={run.provider} model={run.model} />
        </td>
      ) : null}
      <td className="px-3 py-2">
        <TurnChip turn={turns} />
      </td>
      <td className="px-3 py-2">{formatUSD(run.costUSD)}</td>
      <td className="px-3 py-2">{run.promptTokens ?? 0} / {run.completionTokens ?? 0}</td>
      <td className="px-3 py-2">{formatDuration(durationMs)}</td>
      {showActions ? (
        <td className="px-3 py-2 text-right">
          <div className="flex gap-2 justify-end opacity-0 group-hover:opacity-100 transition-opacity">
            {onView ? (
              <button className="btn-secondary btn-icon" aria-label="View" onClick={() => run.runId && onView(run.runId!)}>
                <IconChevron />
              </button>
            ) : null}
            {run.state === 'running' && onCancel && run.runId ? (
              <button className="btn-secondary btn-icon" aria-label="Cancel" onClick={() => onCancel(run.runId!)}>
                <IconDelete />
              </button>
            ) : null}
          </div>
        </td>
      ) : null}
    </tr>
  );
}
","src/renderer/components/ui/Tooltip.tsx":"import React, { useEffect, useLayoutEffect, useRef, useState } from 'react';
import { createPortal } from 'react-dom';

export type TooltipProps = {
  children: React.ReactElement;
  content: React.ReactNode;
  placement?: 'top' | 'bottom' | 'left' | 'right';
  delayMs?: number;
  disabled?: boolean;
};

export default function Tooltip({ children, content, placement = 'right', delayMs = 200, disabled }: TooltipProps) {
  const [open, setOpen] = useState(false);
  const [position, setPosition] = useState<{ top: number; left: number } | null>(null);
  const timerRef = useRef<number | null>(null);
  const anchorRef = useRef<HTMLElement | null>(null);
  const tooltipRef = useRef<HTMLDivElement | null>(null);

  const child = React.Children.only(children);

  useEffect(() => () => { if (timerRef.current) window.clearTimeout(timerRef.current); }, []);

  useLayoutEffect(() => {
    if (!open) {
      setPosition(null);
      return;
    }
    if (!anchorRef.current) return;
    setPosition({ top: -9999, left: -9999 });
  }, [open, placement]);

  useLayoutEffect(() => {
    if (!open || !position || position.top !== -9999 || !tooltipRef.current || !anchorRef.current) return;

    const tip = tooltipRef.current;
    const tipRect = tip.getBoundingClientRect();
    const anchor = anchorRef.current;
    const anchorRect = anchor.getBoundingClientRect();
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;
    const spacing = 8;

    const calcPos = (pl: string) => {
      let t = 0, l = 0;
      switch (pl) {
        case 'right':
          t = anchorRect.top + anchorRect.height / 2 - tipRect.height / 2;
          l = anchorRect.right + spacing;
          break;
        case 'left':
          t = anchorRect.top + anchorRect.height / 2 - tipRect.height / 2;
          l = anchorRect.left - spacing - tipRect.width;
          break;
        case 'top':
          t = anchorRect.top - spacing - tipRect.height;
          l = anchorRect.left + anchorRect.width / 2 - tipRect.width / 2;
          break;
        case 'bottom':
          t = anchorRect.bottom + spacing;
          l = anchorRect.left + anchorRect.width / 2 - tipRect.width / 2;
          break;
      }
      return { top: t, left: l };
    };

    const fits = (p: { top: number; left: number }) => {
      return p.left >= 0 && p.top >= 0 && p.left + tipRect.width <= viewportWidth && p.top + tipRect.height <= viewportHeight;
    };

    let bestPlacement = placement;
    let pos = calcPos(placement);

    if (!fits(pos)) {
      let opposite: string;
      if (placement === 'right') opposite = 'left';
      else if (placement === 'left') opposite = 'right';
      else if (placement === 'top') opposite = 'bottom';
      else opposite = 'top';

      const oppPos = calcPos(opposite);
      if (fits(oppPos)) {
        bestPlacement = opposite;
        pos = oppPos;
      }
    }

    // Clamp to viewport
    pos.top = Math.max(0, Math.min(pos.top, viewportHeight - tipRect.height));
    pos.left = Math.max(0, Math.min(pos.left, viewportWidth - tipRect.width));

    setPosition({
      top: pos.top + window.pageYOffset,
      left: pos.left + window.pageXOffset,
    });
  }, [open, position, placement]);

  const show = () => {
    if (disabled) return;
    if (timerRef.current) window.clearTimeout(timerRef.current);
    timerRef.current = window.setTimeout(() => setOpen(true), delayMs) as any;
  };
  const hide = () => {
    if (timerRef.current) window.clearTimeout(timerRef.current);
    setOpen(false);
  };

  return (
    <>
      {React.cloneElement(child, {
        ref: (el: HTMLElement) => {
          const anyChild: any = child as any;
          if (typeof anyChild.ref === 'function') anyChild.ref(el);
          else if (anyChild.ref) (anyChild.ref as any).current = el;
          anchorRef.current = el;
        },
        onMouseEnter: (e: any) => { child.props.onMouseEnter?.(e); show(); },
        onMouseLeave: (e: any) => { child.props.onMouseLeave?.(e); hide(); },
        onFocus: (e: any) => { child.props.onFocus?.(e); show(); },
        onBlur: (e: any) => { child.props.onBlur?.(e); hide(); },
        'aria-describedby': open ? 'tooltip' : undefined,
      })}
      {open && position && createPortal(
        <div className="ui-tooltip" role="tooltip" ref={tooltipRef} style={{ position: 'absolute', top: position.top, left: position.left }}>
          <div className="ui-tooltip__content">{content}</div>
        </div>,
        document.body
      )}
    </>
  );
}
","packages/factory-ts/src/pricing.ts":"import fs from 'node:fs';
import path from 'node:path';
import fetch from 'node-fetch';

export type ModelPrice = {
  provider: string; // e.g., openai
  model: string;    // e.g., gpt-4o-mini
  inputPerMTokensUSD: number;    // cost per 1,000,000 input tokens in USD
  outputPerMTokensUSD: number;   // cost per 1,000,000 output tokens in USD
  currency?: 'USD';
};

export type PricingState = {
  updatedAt: string;
  prices: ModelPrice[];
};

export type PricingConfig = {
  // Optional mapping to JSON endpoints you can refresh from per provider
  supplierUrls?: Record<string, string>;
};

function ensureDir(p: string) {
  try { fs.mkdirSync(p, { recursive: true }); } catch {}
}

function nowIso() { return new Date().toISOString(); }

export class PricingManager {
  private readonly root: string;
  private readonly storeFile: string;
  private readonly configFile: string;
  private state: PricingState;
  private readonly defaults: PricingState;

  constructor(projectRoot?: string, defaults?: PricingState) {
    this.root = path.resolve(projectRoot || process.cwd());
    const confDir = path.join(this.root, '.factory');
    ensureDir(confDir);
    this.storeFile = path.join(confDir, 'prices.json');
    this.configFile = path.join(confDir, 'pricing.config.json');
    this.defaults = defaults || { updatedAt: nowIso(), prices: loadBuiltInDefaults() };
    this.state = this.loadFromDisk() || this.defaults;
  }

  private loadFromDisk(): PricingState | null {
    try {
      const raw = fs.readFileSync(this.storeFile, 'utf8');
      const parsed = JSON.parse(raw);
      if (parsed && Array.isArray(parsed.prices)) return parsed as PricingState;
    } catch {}
    return null;
  }

  private saveToDisk() {
    try {
      ensureDir(path.dirname(this.storeFile));
      fs.writeFileSync(this.storeFile, JSON.stringify(this.state, null, 2), 'utf8');
    } catch {}
  }

  listPrices(): PricingState {
    return this.state;
  }

  upsertPrices(prices: ModelPrice[], updatedAt?: string) {
    const map = new Map<string, ModelPrice>();
    for (const p of this.state.prices) map.set(`${p.provider}:${p.model}`, p);
    for (const p of prices) map.set(`${p.provider}:${p.model}`, p);
    this.state = { updatedAt: updatedAt || nowIso(), prices: Array.from(map.values()) };
    this.saveToDisk();
  }

  getPrice(provider: string | undefined, model: string | undefined): ModelPrice | undefined {
    if (!provider || !model) return undefined;
    const key = `${provider}:${model}`.toLowerCase();
    return this.state.prices.find(p => `${p.provider}:${p.model}`.toLowerCase() === key);
  }

  async refresh(provider?: string, urlOverride?: string): Promise<PricingState> {
    const cfg = this.loadConfig();
    const provs = provider ? [provider] : Object.keys(cfg.supplierUrls || {});
    const newPrices: ModelPrice[] = [];

    for (const prov of provs) {
      const url = urlOverride || cfg.supplierUrls?.[prov];
      if (!url) continue;
      try {
        const res = await fetch(url);
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const data = await res.json();
        // Expect either array of ModelPrice or an object { prices: ModelPrice[] }
        const arr = Array.isArray(data) ? data : (Array.isArray(data?.prices) ? data.prices : []);
        for (const raw of arr) {
          // Best-effort normalization
          if (raw && raw.provider && raw.model) {
            const input = Number(raw.inputPerMTokensUSD ?? raw.input_per_m_tokens_usd ?? raw.inputPerKTokensUSD * 1000 );
            const output = Number(raw.outputPerMTokensUSD ?? raw.output_per_m_tokens_usd ?? raw.outputPerKTokensUSD * 1000 );
            if (!isFinite(input) || !isFinite(output)) continue;
            newPrices.push({ provider: String(raw.provider), model: String(raw.model), inputPerMTokensUSD: input, outputPerMTokensUSD: output, currency: 'USD' });
          }
        }
      } catch (e) {
        // Ignore provider errors; keep previous values
      }
    }
    if (newPrices.length) this.upsertPrices(newPrices);
    return this.state;
  }

  loadConfig(): PricingConfig {
    try {
      const raw = fs.readFileSync(this.configFile, 'utf8');
      const parsed = JSON.parse(raw);
      return parsed || {};
    } catch {}
    return { supplierUrls: {} };
  }

  saveConfig(cfg: PricingConfig) {
    ensureDir(path.dirname(this.configFile));
    fs.writeFileSync(this.configFile, JSON.stringify(cfg, null, 2), 'utf8');
  }
}

function loadBuiltInDefaults(): ModelPrice[] {
  try {
    const p = path.join(process.cwd(), 'packages', 'factory-ts', 'assets', 'default-prices.json');
    const raw = fs.readFileSync(p, 'utf8');
    const data = JSON.parse(raw) as { prices: ModelPrice[] };
    const arr: ModelPrice[] = Array.isArray(data) ? data : (Array.isArray(data?.prices) ? data.prices : []);
    return arr
  } catch {
    // Minimal safe defaults if asset missing
    return [
      { provider: 'openai', model: 'gpt-4o-mini', inputPerMTokensUSD: 150, outputPerMTokensUSD: 600, currency: 'USD' },
      { provider: 'openai', model: 'gpt-4o', inputPerMTokensUSD: 5000, outputPerMTokensUSD: 15000, currency: 'USD' },
      { provider: 'openai', model: 'gpt-3.5-turbo', inputPerMTokensUSD: 1500, outputPerMTokensUSD: 2000, currency: 'USD' },
    ];
  }
}

export function createPricingManager(opts?: { projectRoot?: string; defaults?: PricingState }) {
  return new PricingManager(opts?.projectRoot, opts?.defaults);
}

export function estimateCostUSD(tokensIn: number, tokensOut: number, price?: ModelPrice): number | undefined {
  if (!price) return undefined;
  const inCost = (tokensIn / 1_000_000) * price.inputPerMTokensUSD;
  const outCost = (tokensOut / 1_000_000) * price.outputPerMTokensUSD;
  const total = inCost + outCost;
  return isFinite(total) ? Number(total.toFixed(8)) : undefined;
}
","src/renderer/screens/AgentsView.tsx":"import React, { useEffect, useMemo, useState } from 'react';
import { useAgents } from '../hooks/useAgents';
import { useActiveProject } from '../projects/ProjectContext';
import type { AgentRun } from '../services/agentsService';
import ChatConversation from '../components/agents/ChatConversation';
import AgentRunRow from '../components/agents/AgentRunRow';
import ModelChip from '../components/agents/ModelChip';

function formatUSD(n?: number) {
  if (n == null) return '\u2014';
  return `$${n.toFixed(4)}`;
}

function formatTime(iso?: string) {
  if (!iso) return '';
  try {
    const d = new Date(iso);
    return d.toLocaleTimeString();
  } catch {
    return iso ?? '';
  }
}

export default function AgentsView() {
  const { runs, activeRuns, cancelRun } = useAgents();
  const { projectId } = useActiveProject();
  const [openRunId, setOpenRunId] = useState<string | null>(null);

  const projectRuns = useMemo(() => runs.filter(r => r.projectId === projectId), [runs, projectId]);
  const activeProjectRuns = useMemo(() => activeRuns.filter(r => r.projectId === projectId), [activeRuns, projectId]);

  useEffect(() => {
    const hash = (window.location.hash || '').replace(/^#/, '');
    const m = /^agents\/run\/(.+)$/.exec(hash);
    if (m && m[1]) {
      const id = m[1];
      setTimeout(() => {
        const el = document.getElementById(`run-${id}`);
        if (el) {
          el.scrollIntoView({ block: 'center', behavior: 'smooth' });
          el.classList.add('highlighted');
          setTimeout(() => el.classList.remove('highlighted'), 2000);
        }
      }, 0);
    }
  }, [projectRuns.length]);

  const findRunById = (id: string | null): AgentRun | undefined => {
    if (!id) return undefined;
    return projectRuns.find(r => r.runId === id) || activeProjectRuns.find(r => r.runId === id);
  };

  const selectedRun = findRunById(openRunId || '');

  return (
    <div className="flex-1 overflow-auto">
      <div className="px-4 py-3 border-b border-neutral-200 dark:border-neutral-800">
        <div className="text-lg font-semibold">Agents</div>
        <div className="text-sm text-neutral-600 dark:text-neutral-400">Monitor running agents, usage and costs</div>
      </div>

      <div className="p-4 space-y-6">
        <section>
          <div className="flex items-center justify-between mb-2">
            <h2 className="text-sm font-semibold text-neutral-800 dark:text-neutral-200">Active ({activeProjectRuns.length})</h2>
          </div>
          {activeProjectRuns.length === 0 ? (
            <div className="text-sm text-neutral-500">No active agents.</div>
          ) : (
            <div className="overflow-auto border rounded-md border-neutral-200 dark:border-neutral-800">
              <table className="min-w-full text-sm">
                <thead className="bg-neutral-50 dark:bg-neutral-800/50 text-neutral-600 dark:text-neutral-400">
                  <tr>
                    <th className="text-left px-3 py-2">Run</th>
                    <th className="text-left px-3 py-2">Task</th>
                    <th className="text-left px-3 py-2">Status</th>
                    <th className="text-left px-3 py-2">Model</th>
                    <th className="text-left px-3 py-2">Turn</th>
                    <th className="text-left px-3 py-2">Cost</th>
                    <th className="text-left px-3 py-2">Tokens</th>
                    <th className="text-left px-3 py-2">Duration</th>
                    <th className="text-right px-3 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {activeProjectRuns.map(r => (
                    <AgentRunRow key={r.runId ?? Math.random().toString(36)} run={r} onView={(id) => setOpenRunId(id)} onCancel={(id) => cancelRun(id)} showModel />
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </section>

        <section>
          <div className="flex items-center justify-between mb-2">
            <h2 className="text-sm font-semibold text-neutral-800 dark:text-neutral-200">History ({projectRuns.length})</h2>
          </div>
          {projectRuns.length === 0 ? (
            <div className="text-sm text-neutral-500">No runs yet for this project.</div>
          ) : (
            <div className="overflow-auto border rounded-md border-neutral-200 dark:border-neutral-800">
              <table className="min-w-full text-sm">
                <thead className="bg-neutral-50 dark:bg-neutral-800/50 text-neutral-600 dark:text-neutral-400">
                  <tr>
                    <th className="text-left px-3 py-2">Run</th>
                    <th className="text-left px-3 py-2">Task</th>
                    <th className="text-left px-3 py-2">Status</th>
                    <th className="text-left px-3 py-2">Model</th>
                    <th className="text-left px-3 py-2">Turn</th>
                    <th className="text-left px-3 py-2">Cost</th>
                    <th className="text-left px-3 py-2">Tokens</th>
                    <th className="text-left px-3 py-2">Duration</th>
                    <th className="text-right px-3 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {projectRuns.map(r => (
                    <AgentRunRow key={r.runId ?? Math.random().toString(36)} run={r} onView={(id) => setOpenRunId(id)} onCancel={(id) => cancelRun(id)} showModel />
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </section>
      </div>

      {selectedRun ? (
        <div className="fixed inset-0 z-50 flex items-center justify-center">
          <div className="absolute inset-0 bg-black/40" onClick={() => setOpenRunId(null)} />
          <div className="relative bg-white dark:bg-neutral-950 rounded-lg shadow-xl w-[92vw] max-w-5xl max-h-[90vh] border border-neutral-200 dark:border-neutral-800">
            <div className="px-4 py-3 border-b border-neutral-200 dark:border-neutral-800 flex items-center justify-between">
              <div className="min-w-0">
                <div className="font-semibold text-sm truncate">Run #{selectedRun.runId.slice(0,8)} \u00b7 {selectedRun.taskId ?? 'Task'}{selectedRun.featureId ? ` \u00b7 Feature ${selectedRun.featureId}` : ''}</div>
                <div className="text-xs text-neutral-500 truncate flex items-center gap-2">
                  <ModelChip provider={selectedRun.provider} model={selectedRun.model} />
                  <span>\u00b7 {selectedRun.state} \u00b7 Updated {formatTime(selectedRun.updatedAt)}</span>
                </div>
              </div>
              <button className="btn-secondary" onClick={() => setOpenRunId(null)}>Close</button>
            </div>
            <div className="p-4">
              <ChatConversation run={selectedRun} />
            </div>
          </div>
        </div>
      ) : null}
    </div>
  );
}
","src/renderer/components/agents/ModelChip.tsx":"import React from 'react';

function providerLabel(p?: string) {
  if (!p) return '';
  const key = String(p).toLowerCase();
  const map: Record<string, string> = {
    openai: 'OpenAI',
    anthropic: 'Anthropic',
    gemini: 'Gemini',
    google: 'Google',
    xai: 'xAI',
    groq: 'Groq',
    together: 'Together',
    azure: 'Azure',
    ollama: 'Ollama',
    local: 'Local',
    custom: 'Custom',
  };
  return map[key] || p;
}

function providerDotClasses(p?: string) {
  const key = String(p || '').toLowerCase();
  switch (key) {
    case 'openai':
      return 'bg-blue-500';
    case 'anthropic':
      return 'bg-orange-500';
    case 'gemini':
    case 'google':
      return 'bg-indigo-500';
    case 'xai':
      return 'bg-black';
    case 'groq':
      return 'bg-pink-500';
    case 'together':
      return 'bg-emerald-500';
    case 'azure':
      return 'bg-sky-600';
    case 'ollama':
    case 'local':
      return 'bg-neutral-500';
    case 'custom':
      return 'bg-purple-500';
    default:
      return 'bg-neutral-400';
  }
}

export default function ModelChip({ provider, model, className }: { provider?: string; model?: string; className?: string }) {
  const dot = providerDotClasses(provider);
  const prov = providerLabel(provider);
  const parts = [prov || undefined, model || undefined].filter(Boolean);
  const label = parts.join(' ¬∑ ');
  const title = label || 'Unknown model';

  return (
    <span
      className={[
        'inline-flex items-center gap-1 rounded-full border px-2 py-0.5 text-xs font-medium',
        'bg-neutral-100 text-neutral-800 dark:bg-neutral-800/60 dark:text-neutral-200',
        'border-neutral-200 dark:border-neutral-700',
        className || '',
      ].join(' ')}
      title={title}
      aria-label={title}
      role="note"
    >
      <span className={["inline-block w-1.5 h-1.5 rounded-full", dot].join(' ')} aria-hidden />
      <span className="truncate max-w-[18ch]" style={{ lineHeight: 1 }}>{label || '‚Äî'}</span>
    </span>
  );
}
